function calculate({ annual_tuition_fees, years_of_study, annual_opportunity_cost, post_grad_salary, annual_salary_growth, career_length, discount_rate, pre_study_salary_benchmark }) {
    // Input validation and defaults
    annual_tuition_fees = Math.max(0, annual_tuition_fees || 0);
    years_of_study = Math.max(1, years_of_study || 1); 
    annual_opportunity_cost = Math.max(0, annual_opportunity_cost || 0);
    post_grad_salary = Math.max(0, post_grad_salary || 0);
    annual_salary_growth = Math.max(0, annual_salary_growth || 0);
    career_length = Math.max(1, career_length || 1);
    discount_rate = Math.max(0, discount_rate || 0);
    pre_study_salary_benchmark = Math.max(0, pre_study_salary_benchmark || 0);

    const growthFactor = 1 + (annual_salary_growth / 100);
    const discountFactor = 1 + (discount_rate / 100);

    // 1. Calculate Total Direct Educational Costs
    const total_education_cost = annual_tuition_fees * years_of_study;

    // 2. Calculate Total Opportunity Costs (lost income during study)
    const total_opportunity_cost = annual_opportunity_cost * years_of_study;

    // 3. Calculate Total Investment (Direct + Opportunity)
    const total_investment_cost = total_education_cost + total_opportunity_cost;

    // 4. Project Lifetime Earnings with Education
    let total_lifetime_earnings_educated = 0;
    let currentSalaryEducated = post_grad_salary;
    for (let i = 1; i <= career_length; i++) {
        total_lifetime_earnings_educated += currentSalaryEducated;
        currentSalaryEducated *= growthFactor;
    }

    // 5. Project Lifetime Earnings Without Education (Baseline)
    let total_lifetime_earnings_baseline = 0;
    let currentSalaryBaseline = pre_study_salary_benchmark;
    // The baseline career length equals the study period plus the post-study career length, as they work continuously.
    for (let i = 1; i <= years_of_study + career_length; i++) {
        total_lifetime_earnings_baseline += currentSalaryBaseline;
        currentSalaryBaseline *= growthFactor;
    }

    // 6. Calculate Net Financial Gain from Education
    // This is the additional lifetime earnings generated by education, minus all costs associated with it.
    const net_benefit_from_education = total_lifetime_earnings_educated - total_lifetime_earnings_baseline;
    const net_financial_gain = net_benefit_from_education - total_education_cost; // Opportunity cost is already accounted for in baseline vs educated earnings comparison

    // 7. Calculate Return on Investment (ROI)
    let roi_percentage = 0;
    if (total_investment_cost > 0) {
        roi_percentage = (net_benefit_from_education / total_investment_cost) * 100; 
    } else if (net_benefit_from_education > 0) {
        roi_percentage = Infinity; // Pure gain with no cost
    }

    // 8. Calculate Net Present Value (NPV) of Education
    // We compare the year-by-year cash flows of the educated path versus the baseline path.
    let net_cash_flows_comparison = [];
    let currentEducatedSalary_NPV = post_grad_salary;
    let currentBaselineSalary_NPV = pre_study_salary_benchmark;

    // During study years (negative impact on cash flow from education)
    for (let i = 1; i <= years_of_study; i++) {
        const cf_educated_year = -(annual_tuition_fees + annual_opportunity_cost); // Costs for educated path
        const cf_baseline_year = currentBaselineSalary_NPV; // Earnings for baseline path
        net_cash_flows_comparison.push(cf_educated_year - cf_baseline_year);
        currentBaselineSalary_NPV *= growthFactor;
    }

    // During career years (positive impact on cash flow from education)
    for (let i = 1; i <= career_length; i++) {
        const cf_educated_year = currentEducatedSalary_NPV; // Earnings for educated path
        const cf_baseline_year = currentBaselineSalary_NPV; // Earnings for baseline path
        net_cash_flows_comparison.push(cf_educated_year - cf_baseline_year);
        currentEducatedSalary_NPV *= growthFactor;
        currentBaselineSalary_NPV *= growthFactor;
    }

    let npv_education = 0;
    for (let i = 0; i < net_cash_flows_comparison.length; i++) {
        npv_education += net_cash_flows_comparison[i] / Math.pow(discountFactor, i + 1);
    }

    // Return calculated outputs
    return {
        total_education_cost: total_education_cost,
        total_opportunity_cost: total_opportunity_cost,
        total_investment_cost: total_investment_cost,
        total_lifetime_earnings_educated: total_lifetime_earnings_educated,
        total_lifetime_earnings_baseline: total_lifetime_earnings_baseline,
        net_financial_gain: net_financial_gain,
        roi_percentage: roi_percentage,
        npv_education: npv_education,
    };
}
