function calculate({ currentFederalGrants, federalGrantShiftPercent, currentStateAid, stateAidShiftPercent, currentLocalTaxAllocation, localTaxShiftPercent, currentOperatingBudget, inflationFactor, existingProgramToAnalyzeCost, newProgramConsiderationCost }) {
    // Default optional inputs to 0 if not provided or invalid
    existingProgramToAnalyzeCost = typeof existingProgramToAnalyzeCost === 'number' && !isNaN(existingProgramToAnalyzeCost) ? existingProgramToAnalyzeCost : 0;
    newProgramConsiderationCost = typeof newProgramConsiderationCost === 'number' && !isNaN(newProgramConsiderationCost) ? newProgramConsiderationCost : 0;

    // Step 1: Calculate new funding for each source based on percentage shifts
    const newFederalGrants = currentFederalGrants * (1 + federalGrantShiftPercent / 100);
    const newStateAid = currentStateAid * (1 + stateAidShiftPercent / 100);
    const newLocalTaxAllocation = currentLocalTaxAllocation * (1 + localTaxShiftPercent / 100);

    // Step 2: Calculate total funding shifts and new total available funding
    const totalCurrentFunding = currentFederalGrants + currentStateAid + currentLocalTaxAllocation;
    const newTotalFunding = newFederalGrants + newStateAid + newLocalTaxAllocation;
    const totalFundingChange = newTotalFunding - totalCurrentFunding;

    // Step 3: Calculate the inflation-adjusted baseline operational budget
    const requiredOperationalBudget = currentOperatingBudget * (1 + inflationFactor / 100);

    // Step 4: Determine the operational funding gap or surplus after covering baseline operations
    const fundingGapOrSurplus = newTotalFunding - requiredOperationalBudget;

    // Step 5: Evaluate impact on the specific existing program (if provided)
    let existingProgramImpact = 'N/A - No specific program provided for analysis.';
    if (existingProgramToAnalyzeCost > 0) {
        if (fundingGapOrSurplus >= existingProgramToAnalyzeCost) {
            existingProgramImpact = 'Fully Funded (Surplus after baseline operations covers program cost)';
        } else if (fundingGapOrSurplus >= 0 && fundingGapOrSurplus < existingProgramToAnalyzeCost) {
            existingProgramImpact = 'Partially Funded / Requires Additional Reallocation (Surplus covers a portion)';
        } else { // fundingGapOrSurplus < 0, meaning a deficit even for baseline operations
            existingProgramImpact = 'Likely Impacted or Eliminated (Overall deficit requires significant reallocation or cuts)';
        }
    }

    // Step 6: Evaluate viability of the new program under consideration (if provided)
    let newProgramViability = 'N/A - No new program provided for consideration.';
    if (newProgramConsiderationCost > 0) {
        if (fundingGapOrSurplus >= newProgramConsiderationCost) {
            newProgramViability = 'Feasible (Surplus after baseline operations covers new program cost)';
        } else if (fundingGapOrSurplus > 0 && fundingGapOrSurplus < newProgramConsiderationCost) {
            newProgramViability = 'Partially Feasible (Surplus covers only a portion, requires additional funding)';
        } else {
            newProgramViability = 'Not Feasible (No surplus or significant deficit after baseline operations)';
        }
    }

    return {
        totalFundingChange,
        newTotalFunding,
        requiredOperationalBudget,
        fundingGapOrSurplus,
        existingProgramImpact,
        newProgramViability
    };
}
