function calculateEquityImpact(inputs) {
    const {
        targetGroupStudents,
        totalStudentPopulation,
        baselineAchievementGap,
        baselineResourceDisparityIndex,
        policyInvestmentPerTargetStudent,
        policyEffectivenessRate,
        policyImplementationFidelity,
        existingSupportSaturation
    } = inputs;

    // Input validation and edge cases
    if (targetGroupStudents <= 0 || totalStudentPopulation <= 0 || baselineAchievementGap < 0 || baselineResourceDisparityIndex < 0 || policyInvestmentPerTargetStudent < 0 || policyEffectivenessRate < 0 || policyImplementationFidelity < 0 || existingSupportSaturation < 0) {
        return {
            projectedAchievementGapReduction: 0,
            projectedResourceEquityImprovement: 0,
            overallEquityImpactScore: 0,
            costPerGapPointReduction: Infinity
        };
    }

    // Handle scenarios where equity is already perfect or cannot be improved meaningfully
    if (baselineAchievementGap === 0 && baselineResourceDisparityIndex === 0) {
         return {
            projectedAchievementGapReduction: 0,
            projectedResourceEquityImprovement: 0,
            overallEquityImpactScore: 100, // Already perfect equity
            costPerGapPointReduction: 0 // No cost for reduction as there's no gap
        };
    }

    // 1. Calculate Adjusted Policy Effectiveness
    // Fidelity factor: How well the policy is actually implemented.
    const fidelityFactor = policyImplementationFidelity / 100;
    // Saturation discount: Diminishing returns if existing support is already high.
    // Minimum 0.1 factor ensures some effectiveness even at high saturation.
    const saturationDiscount = 1 - (existingSupportSaturation / 100);
    const effectiveSaturationFactor = Math.max(0.1, saturationDiscount);

    // The effective rate combines the policy's theoretical effectiveness with real-world implementation and existing support.
    const adjustedPolicyEffectivenessPercentage = policyEffectivenessRate * fidelityFactor * effectiveSaturationFactor;

    // 2. Projected Achievement Gap Reduction
    // Calculate the raw reduction, then cap it at the existing baseline gap.
    let projectedAchievementGapReduction = (baselineAchievementGap * adjustedPolicyEffectivenessPercentage) / 100;
    projectedAchievementGapReduction = Math.min(projectedAchievementGapReduction, baselineAchievementGap);
    projectedAchievementGapReduction = Math.max(0, projectedAchievementGapReduction); // Ensure non-negative

    // 3. Projected Resource Equity Improvement
    // Potential improvement is the difference between current disparity and perfect equity (1.0).
    const potentialEquityImprovement = 1 - baselineResourceDisparityIndex;

    // Investment influence: Scale the per-student investment to an index impact.
    // (e.g., $1000 investment might translate to 0.01 index improvement)
    // Multiply by fidelity as poorly implemented investments yield less equity.
    const investmentInfluence = (policyInvestmentPerTargetStudent / 1000) * fidelityFactor;

    // The projected improvement is capped by the maximum possible improvement and ensured to be non-negative.
    let projectedResourceEquityImprovement = Math.min(potentialEquityImprovement, investmentInfluence);
    projectedResourceEquityImprovement = Math.max(0, projectedResourceEquityImprovement); // Ensure non-negative

    // 4. Overall Equity Impact Score (0-100)
    // Normalize achievement gap reduction by baseline gap to get a percentage of the gap closed.
    const percentGapClosed = baselineAchievementGap > 0 ? (projectedAchievementGapReduction / baselineAchievementGap) : 0;
    // Normalize resource equity improvement by potential improvement to get a percentage of potential achieved.
    const percentEquityImproved = potentialEquityImprovement > 0 ? (projectedResourceEquityImprovement / potentialEquityImprovement) : 0;

    // Weighted average: Achievement (60%) often seen as more direct outcome, Resource Equity (40%) as foundational.
    let overallEquityImpactScore = (percentGapClosed * 60) + (percentEquityImproved * 40);
    overallEquityImpactScore = Math.min(100, Math.max(0, overallEquityImpactScore)); // Cap between 0 and 100

    // 5. Cost Per Gap Point Reduction
    let costPerGapPointReduction = Infinity;
    const totalPolicyCost = policyInvestmentPerTargetStudent * targetGroupStudents;

    if (projectedAchievementGapReduction > 0) {
        costPerGapPointReduction = totalPolicyCost / projectedAchievementGapReduction;
    } else if (baselineAchievementGap === 0 && totalPolicyCost > 0) {
        // If there's no gap to reduce but money is spent, it's considered cost for maintaining or zero efficiency for *reduction*.
        // If the gap is 0, any cost for 'reduction' effectively means an infinite cost per point reduction, 
        // but a 0 result signifies that no further reduction is needed.
        costPerGapPointReduction = 0; // Represents no cost incurred for 'reducing' a non-existent gap.
    }

    return {
        projectedAchievementGapReduction: parseFloat(projectedAchievementGapReduction.toFixed(2)),
        projectedResourceEquityImprovement: parseFloat(projectedResourceEquityImprovement.toFixed(3)),
        overallEquityImpactScore: parseFloat(overallEquityImpactScore.toFixed(1)),
        costPerGapPointReduction: parseFloat(costPerGapPointReduction.toFixed(2))
    };
}
