function calculate({ currentDistrictVAP, proposedDistrictVAP, currentDemogA_VAP_pct, proposedDemogA_VAP_pct, currentDemogB_VAP_pct, proposedDemogB_VAP_pct, demogA_HistoricalTurnout, demogA_PartyASupport, demogB_HistoricalTurnout, demogB_PartyASupport }) {

    // Input Validation and Normalization
    const validateAndNormalize = (val, min, max, defaultVal) => {
        let num = parseFloat(val);
        if (isNaN(num) || num < min || num > max) return defaultVal;
        return num;
    };

    currentDistrictVAP = validateAndNormalize(currentDistrictVAP, 0, Infinity, 0);
    proposedDistrictVAP = validateAndNormalize(proposedDistrictVAP, 0, Infinity, 0);

    let currentDemogA_VAP_pct_norm = validateAndNormalize(currentDemogA_VAP_pct, 0, 100, 0) / 100;
    let proposedDemogA_VAP_pct_norm = validateAndNormalize(proposedDemogA_VAP_pct, 0, 100, 0) / 100;
    let currentDemogB_VAP_pct_norm = validateAndNormalize(currentDemogB_VAP_pct, 0, 100, 0) / 100;
    let proposedDemogB_VAP_pct_norm = validateAndNormalize(proposedDemogB_VAP_pct, 0, 100, 0) / 100;

    demogA_HistoricalTurnout = validateAndNormalize(demogA_HistoricalTurnout, 0, 1, 0);
    demogA_PartyASupport = validateAndNormalize(demogA_PartyASupport, 0, 1, 0);
    demogB_HistoricalTurnout = validateAndNormalize(demogB_HistoricalTurnout, 0, 1, 0);
    demogB_PartyASupport = validateAndNormalize(demogB_PartyASupport, 0, 1, 0);

    // Handle cases where total specified percentages exceed 100 for robustness
    const currentSumPct = currentDemogA_VAP_pct_norm + currentDemogB_VAP_pct_norm;
    const proposedSumPct = proposedDemogA_VAP_pct_norm + proposedDemogB_VAP_pct_norm;

    if (currentSumPct > 1) {
        const scale = 1 / currentSumPct;
        currentDemogA_VAP_pct_norm *= scale;
        currentDemogB_VAP_pct_norm *= scale;
    }
    if (proposedSumPct > 1) {
        const scale = 1 / proposedSumPct;
        proposedDemogA_VAP_pct_norm *= scale;
        proposedDemogB_VAP_pct_norm *= scale;
    }

    const currentDemogOther_VAP_pct = Math.max(0, 1 - (currentDemogA_VAP_pct_norm + currentDemogB_VAP_pct_norm));
    const proposedDemogOther_VAP_pct = Math.max(0, 1 - (proposedDemogA_VAP_pct_norm + proposedDemogB_VAP_pct_norm));

    // Assume 'Other' group's characteristics are an average of A and B, or default if A+B are zero
    const otherDemographicsHistoricalTurnout = (demogA_HistoricalTurnout + demogB_HistoricalTurnout) / 2 || 0.5; // Default to 50%
    const otherDemographicsPartyASupport = (demogA_PartyASupport + demogB_PartyASupport) / 2 || 0.5; // Default to 50%

    // If no population data, return zeros
    if (currentDistrictVAP === 0 && proposedDistrictVAP === 0) {
      return {
        demographicShiftIndex: 0,
        projectedOverallTurnoutChange: 0,
        projectedPartyA_VoteShare: 0,
        communityCohesionImpactScore: 0
      };
    }

    // Output 1: Demographic Shift Index (0-100)
    const shiftA = Math.abs(proposedDemogA_VAP_pct_norm - currentDemogA_VAP_pct_norm);
    const shiftB = Math.abs(proposedDemogB_VAP_pct_norm - currentDemogB_VAP_pct_norm);
    const shiftOther = Math.abs(proposedDemogOther_VAP_pct - currentDemogOther_VAP_pct);
    const demographicShiftIndex = Math.min(100, (shiftA + shiftB + shiftOther) * 100 / 2); // Sum of absolute differences for each group. Divide by 2 as each shift is 'counted' twice for a total change of up to 200% if one group completely replaces another. Scale to 100%.

    // Output 2: Projected Overall Turnout Change
    const calculateProjectedTurnout = (vap, demogA_pct, demogB_pct, demogOther_pct) => {
        if (vap <= 0) return 0;
        const turnoutA = vap * demogA_pct * demogA_HistoricalTurnout;
        const turnoutB = vap * demogB_pct * demogB_HistoricalTurnout;
        const turnoutOther = vap * demogOther_pct * otherDemographicsHistoricalTurnout;
        return turnoutA + turnoutB + turnoutOther;
    };

    const currentProjectedTurnout = calculateProjectedTurnout(
        currentDistrictVAP,
        currentDemogA_VAP_pct_norm,
        currentDemogB_VAP_pct_norm,
        currentDemogOther_VAP_pct
    );
    const proposedProjectedTurnout = calculateProjectedTurnout(
        proposedDistrictVAP,
        proposedDemogA_VAP_pct_norm,
        proposedDemogB_VAP_pct_norm,
        proposedDemogOther_VAP_pct
    );

    let projectedOverallTurnoutChange = 0;
    if (currentProjectedTurnout > 0) {
        projectedOverallTurnoutChange = ((proposedProjectedTurnout - currentProjectedTurnout) / currentProjectedTurnout) * 100;
    } else if (proposedProjectedTurnout > 0) {
        projectedOverallTurnoutChange = Infinity; // Infinite increase from zero turnout
    }

    // Output 3: Projected Party A Vote Share
    let projectedPartyA_VoteShare = 0;
    if (proposedProjectedTurnout > 0) {
        const partyAVotesA = proposedDistrictVAP * proposedDemogA_VAP_pct_norm * demogA_HistoricalTurnout * demogA_PartyASupport;
        const partyAVotesB = proposedDistrictVAP * proposedDemogB_VAP_pct_norm * demogB_HistoricalTurnout * demogB_PartyASupport;
        const partyAVotesOther = proposedDistrictVAP * proposedDemogOther_VAP_pct * otherDemographicsHistoricalTurnout * otherDemographicsPartyASupport;
        const totalPartyAVotes = partyAVotesA + partyAVotesB + partyAVotesOther;
        projectedPartyA_VoteShare = (totalPartyAVotes / proposedProjectedTurnout) * 100;
    }

    // Output 4: Community Cohesion Impact Score (-5 to +5)
    let communityCohesionImpactScore = 0;
    const MAX_SCORE = 5;

    // Factor 1: Demographic Shift Magnitude (Higher shift index implies lower cohesion)
    // Score impact is stronger for larger shifts.
    communityCohesionImpactScore -= (demographicShiftIndex / 100) * (MAX_SCORE * 0.7); // Up to 70% of max negative score

    // Factor 2: Significant VAP change might suggest fragmentation
    if (currentDistrictVAP > 0) {
        const vapChangeRatio = Math.abs(proposedDistrictVAP - currentDistrictVAP) / currentDistrictVAP;
        if (vapChangeRatio > 0.20) { // More than 20% VAP change
            // Larger changes lead to more negative impact, scaled up to 30% of max negative score
            communityCohesionImpactScore -= Math.min(MAX_SCORE * 0.3, (vapChangeRatio - 0.20) * (MAX_SCORE * 0.5)); 
        }
    }

    // Factor 3: Extreme shift in a single demographic group
    const maxIndividualDemogShift = Math.max(shiftA, shiftB, shiftOther);
    if (maxIndividualDemogShift > 0.20) { // If one group shifts by more than 20% of VAP
        // Additional negative impact for very large single-group shifts, up to 20% of max negative score
        communityCohesionImpactScore -= Math.min(MAX_SCORE * 0.2, (maxIndividualDemogShift - 0.20) * (MAX_SCORE * 0.8));
    }

    // Cap the score between -5 and +5
    communityCohesionImpactScore = Math.max(-MAX_SCORE, Math.min(MAX_SCORE, communityCohesionImpactScore));
    // If the demographic shift index is very low, make cohesion score positive or neutral
    if (demographicShiftIndex < 5 && vapChangeRatio < 0.05) {
        communityCohesionImpactScore = Math.max(0, communityCohesionImpactScore); // At least 0 if very stable
    }

    // A perfect score of 0 demographic shift and VAP stability should lead to a higher cohesion score
    if (demographicShiftIndex === 0 && currentDistrictVAP === proposedDistrictVAP) {
      communityCohesionImpactScore = MAX_SCORE; // Max positive if no change at all
    }

    return {
        demographicShiftIndex: demographicShiftIndex,
        projectedOverallTurnoutChange: projectedOverallTurnoutChange,
        projectedPartyA_VoteShare: projectedPartyA_VoteShare,
        communityCohesionImpactScore: communityCohesionImpactScore
    };
}
