const { watts, hours, rate } = inputs;
const w = parseFloat(watts) || 0;
const h = parseFloat(hours) || 0;
const r = parseFloat(rate) || 0;

const dailyKwh = (w * h) / 1000;
const dailyCost = dailyKwh * r;
const monthlyCost = dailyCost * 30.44; // Average days in month
const yearlyCost = dailyCost * 365.25; // Average days in year

return {
  dailyCost,
  monthlyCost,
  yearlyCost
};
