const adults = parseFloat(householdAdults);
const children = parseFloat(householdChildren);
const pets = parseFloat(householdPets);
const duration = parseFloat(durationDays);
const hasMedicalNeeds = medicalNeeds && medicalNeeds.trim() !== '';
const hazards = regionalHazards ? regionalHazards.toLowerCase().split(',').map(h => h.trim()) : [];

// --- Constants for per-person/pet/day needs and base costs ---
const WATER_GALLONS_PER_PERSON_PER_DAY = 1; // Includes pets implicitly for simplicity
const FOOD_CALORIES_ADULT_PER_DAY = 2000;
const FOOD_CALORIES_CHILD_PER_DAY = 1500;
const FOOD_CALORIES_PET_PER_DAY = 300; // Small to medium pet

// Base costs (estimated, can vary greatly)
let costWater = 0;
let costFood = 0;
let costFirstAid = 0;
let costPowerLightingComm = 0;
let costShelterSanitation = 0;
let costPetSupplies = 0;
let costMedicalSupplies = 0;
let costHazardSpecific = 0;

// --- Calculate Core Requirements ---
const totalHumans = adults + children;
const totalLivingBeings = totalHumans + pets;

const waterGallons = totalLivingBeings * WATER_GALLONS_PER_PERSON_PER_DAY * duration;
costWater = waterGallons * 1.75; // Approx cost for bottled water/purification for that volume

const foodCalories = (adults * FOOD_CALORIES_ADULT_PER_DAY + children * FOOD_CALORIES_CHILD_PER_DAY + pets * FOOD_CALORIES_PET_PER_DAY) * duration;
costFood = (adults * 50 + children * 30 + pets * 15) * (duration / 3); // Base cost per 3 days per person/pet equivalent

// --- First Aid Kit ---
let firstAidKitDescription = 'Basic first-aid kit with bandages, antiseptic wipes, pain relievers. '; 
if (totalHumans > 2 || duration > 3) {
    firstAidKitDescription += 'Consider a more comprehensive kit with trauma shears, larger bandages, and specialized medications.';
    costFirstAid = 50 + (totalHumans - 1) * 15 + (duration - 3) * 5; // Base + per person + per day increment
} else {
    costFirstAid = 35;
}

// --- Power, Lighting, Communication ---
let powerSource = 'Multi-purpose radio (hand-crank or solar), power bank for phones, extra batteries for devices. '; 
let lighting = 'Headlamps for each adult, LED lanterns, chemical light sticks. '; 
let communication = 'NOAA weather radio, whistle, charger for mobile devices. ';
costPowerLightingComm = 120 + (adults - 1) * 30 + (duration - 3) * 10;

// --- Shelter & Sanitation ---
let shelterSanitation = 'Emergency blankets (Mylar type), tarp for improvised shelter, plastic sheeting, duct tape. Portable toilet or sanitation bucket with waste bags, hand sanitizer, wet wipes, soap. ';
costShelterSanitation = 60 + (totalHumans - 1) * 20 + (duration - 3) * 5;

// --- Pet Supplies ---
let petSupplies = '';
if (pets > 0) {
    petSupplies = `Pet food (${pets * duration} days supply), water, bowls, leash and collar with ID, carrier, basic pet first-aid, comfort item. `;
    costPetSupplies = pets * (30 + duration * 5);
}

// --- Medical & Special Needs ---
let medicalSupplies = 'Copies of prescription medications, prescription eyeglasses/contact lenses, over-the-counter medications. ';
if (hasMedicalNeeds) {
    medicalSupplies += `**Critical:** Ensure at least ${duration * 1.5} days' supply of ALL specific medical necessities mentioned: ${medicalNeeds}. Consult with your doctor for an emergency supply plan. `;
    costMedicalSupplies = 150 + duration * 10; // Placeholder cost for specialized items
}

// --- Important Documents ---
let importantDocuments = 'Copies of identification (driver\'s license, passport), birth certificates, insurance policies (health, home, auto), medical records, bank account information, deeds/titles, will, emergency contacts. Store in a waterproof, fireproof container.';

// --- Hazard-Specific Recommendations ---
let hazardSpecificRecs = 'Consider a general safety whistle, N95 masks, and a multi-tool. ';

if (hazards.includes('flooding')) {
    hazardSpecificRecs += 'For flooding: Water purification tablets/filter, life vests if near water, sturdy waterproof boots, and an inflatable raft or floatation device. ';
    costHazardSpecific += 75;
}
if (hazards.includes('earthquakes')) {
    hazardSpecificRecs += 'For earthquakes: Sturdy shoes (to protect feet from debris), work gloves, dust mask, whistle, and a pry bar for moving debris. Secure heavy furniture. ';
    costHazardSpecific += 60;
}
if (hazards.includes('blizzards') || hazards.includes('winter storm')) {
    hazardSpecificRecs += 'For blizzards/winter storms: Extra layers of warm clothing, sleeping bags, blankets, chemical hand/foot warmers, shovel, rock salt for ice, non-electric heating source (e.g., propane heater with ventilation). ';
    costHazardSpecific += 100;
}
if (hazards.includes('wildfires')) {
    hazardSpecificRecs += 'For wildfires: N95 or P100 masks, protective eyewear, fire-resistant documents safe, emergency ladder for multi-story homes. Create a defensible space around your home. ';
    costHazardSpecific += 70;
}
if (hazards.includes('tornadoes') || hazards.includes('hurricanes')) {
    hazardSpecificRecs += 'For tornadoes/hurricanes: Weather radio with tone alert, helmet for head protection, sturdy shoes, protective eyewear, pre-identified safe room/shelter. Reinforce windows if possible. ';
    costHazardSpecific += 80;
}

// --- Calculate Total Cost ---
const estimatedTotalCost = costWater + costFood + costFirstAid + costPowerLightingComm + costShelterSanitation + costPetSupplies + costMedicalSupplies + costHazardSpecific;

return {
    estimatedTotalCost: Math.max(0, estimatedTotalCost),
    waterGallonsRequired: waterGallons,
    foodCaloriesRequired: foodCalories,
    firstAidKitLevel: firstAidKitDescription,
    powerSourceRecommendation: powerSource,
    lightingRecommendation: lighting,
    communicationRecommendation: communication,
    shelterSanitationRecommendation: shelterSanitation,
    petSuppliesRecommendation: petSupplies || 'No pets indicated.',
    medicalSuppliesRecommendation: medicalSupplies,
    importantDocumentsRecommendation: importantDocuments,
    hazardSpecificRecommendations: hazardSpecificRecs
};
