function calculate(inputs) {
    const adults = Math.max(0, inputs.adultsCount || 0);
    const children = Math.max(0, inputs.childrenCount || 0);
    const pets = Math.max(0, inputs.petCount || 0);
    let days = Math.max(1, Math.min(14, inputs.daysPreparedFor || 3)); // Cap days between 1 and 14
    const householdSize = adults + children;
    const geoLoc = (inputs.geographicLocation || '').toLowerCase();
    const hasMedicalNeeds = (inputs.hasSpecialMedicalNeeds || '').toLowerCase() === 'yes';

    // Water calculation: 1 gallon per person per day, 0.25 gallon per pet per day
    const totalWaterGallons = (householdSize * days * 1) + (pets * days * 0.25);

    // Food calculation: 3 servings per person per day (approx. 2000 kcal), 1 serving per pet per day
    const totalFoodServings = (householdSize * days * 3) + (pets * days * 1);

    // Textual recommendations
    let firstAidKit = 'Basic first aid kit (bandages, antiseptic wipes, pain relievers, gauze, medical tape, etc.). ';
    if (hasMedicalNeeds) {
        firstAidKit += 'Include at least a 7-day supply of ALL prescription medications, any necessary medical equipment (e.g., glucose monitor, asthma inhaler, infant formula/supplies), and copies of prescriptions and medical information.';
    } else {
        firstAidKit += 'Consider adding any personal medications.';
    }

    let shelterSanitation = 'Sleeping bags/warm blankets for each person, a tent or tarp, sturdy shoes, rain gear. ';
    shelterSanitation += 'Sanitation supplies: moist towelettes, hand sanitizer, garbage bags, plastic ties, toilet paper, feminine hygiene products.';

    let commPowerLight = 'Battery-powered or hand-crank radio (NOAA weather alerts), flashlight(s) with extra batteries, whistle to signal for help. ';
    commPowerLight += 'Power bank for charging mobile phones. USB charging cables for devices.';

    let emergencyDocsCash = 'Copies of important family documents (ID, insurance policies, birth certificates, deeds), stored in a waterproof bag. ';
    emergencyDocsCash += 'Emergency cash in small denominations (ATMs may not work).';

    let specialRisk = 'General recommendation: Develop a family communication plan and designate an out-of-state contact. Practice evacuation routes.';

    if (geoLoc.includes('coastal') || geoLoc.includes('hurricane') || geoLoc.includes('flood')) {
        specialRisk += ' Specific to coastal/flood risks: Waterproof containers for valuables, life vests if near water, sandbags if appropriate, knowledge of evacuation zones and routes.';
    } else if (geoLoc.includes('mountain') || geoLoc.includes('blizzard') || geoLoc.includes('cold')) {
        specialRisk += ' Specific to mountainous/cold risks: Extra layers of warm clothing, emergency blankets, shovel, snow chains for vehicles, knowledge of avalanche safety if applicable.';
    } else if (geoLoc.includes('tornado') || geoLoc.includes('plains')) {
        specialRisk += ' Specific to tornado risks: Helmet for head protection, sturdy shoes, designated safe room or shelter, NOAA weather radio with tone alert.';
    } else if (geoLoc.includes('earthquake') || geoLoc.includes('fault')) {
        specialRisk += ' Specific to earthquake risks: Sturdy shoes, wrench to turn off utilities (gas/water), dust mask, safety goggles, secure heavy furniture.';
    } else if (geoLoc.includes('wildfire') || geoLoc.includes('fire')) {
        specialRisk += ' Specific to wildfire risks: N95 respirator masks, safety goggles, fire extinguisher, clear defensible space around home, go-bag ready for immediate evacuation.';
    } else if (geoLoc.includes('urban')) {
        specialRisk += ' Specific to urban risks: Consider a backpack-style kit for quick evacuation, extra walking shoes, local public transportation maps.';
    }

    return {
        totalWaterGallons: totalWaterGallons,
        totalFoodServings: Math.round(totalFoodServings),
        firstAidKitDetails: firstAidKit,
        shelterSanitationDetails: shelterSanitation,
        commPowerLightDetails: commPowerLight,
        emergencyDocumentsCash: emergencyDocsCash,
        specialRiskPreparedness: specialRisk
    };
}
