formula:
const householdMembers = inputs.householdMembers;
const emergencyDurationDays = inputs.emergencyDurationDays;
const dailyCaloricNeed = inputs.dailyCaloricNeed;
const waterConsumptionRateLiters = inputs.waterConsumptionRateLiters;
const foodCalorieDensity = inputs.foodCalorieDensity;
const safetyMarginPercent = inputs.safetyMarginPercent;

const safetyFactor = 1 + (safetyMarginPercent / 100);

// Calculate total water needed
const baseWaterLiters = householdMembers * emergencyDurationDays * waterConsumptionRateLiters;
const totalWaterLiters = baseWaterLiters * safetyFactor;

// Calculate total caloric need for food
const totalCaloricNeed = householdMembers * emergencyDurationDays * dailyCaloricNeed;

// Calculate total food weight needed based on calorie density
const baseFoodKg = totalCaloricNeed / foodCalorieDensity;
const totalFoodKg = baseFoodKg * safetyFactor;

return {
    totalWaterLiters: totalWaterLiters,
    totalFoodKg: totalFoodKg
};
