const absenteeism_savings = inputs.num_employees * inputs.avg_salary * (inputs.absenteeism_rate_before / 100) * (inputs.expected_absenteeism_reduction / 100);
const productivity_savings = inputs.num_employees * inputs.avg_salary * (inputs.productivity_gain_percent / 100);
const healthcare_savings = inputs.num_employees * inputs.avg_mh_healthcare_cost_per_employee * (inputs.expected_mh_healthcare_reduction / 100);

const total_annual_savings = absenteeism_savings + productivity_savings + healthcare_savings;

let roi_percent;
if (inputs.program_cost > 0) {
    roi_percent = ((total_annual_savings - inputs.program_cost) / inputs.program_cost) * 100;
} else if (total_annual_savings > 0) {
    roi_percent = Infinity; 
} else {
    roi_percent = 0;
}

return {
    total_annual_savings: total_annual_savings,
    roi_percent: roi_percent
};
