function calculate() {
  const mhr = Number(inputs.maximalHeartRate);
  const rhr = Number(inputs.restingHeartRate);
  const age = Number(inputs.age);
  const goal = inputs.trainingGoal.toLowerCase().trim();
  let customLower = inputs.customLowerPercentage ? Number(inputs.customLowerPercentage) : null;
  let customUpper = inputs.customUpperPercentage ? Number(inputs.customUpperPercentage) : null;

  // Input validation
  if (isNaN(mhr) || mhr <= 0) throw new Error('Maximal Heart Rate must be a positive number.');
  if (isNaN(rhr) || rhr <= 0) throw new Error('Resting Heart Rate must be a positive number.');
  if (mhr <= rhr) throw new Error('Maximal Heart Rate must be greater than Resting Heart Rate.');
  if (isNaN(age) || age <= 0) throw new Error('Age must be a positive number.');

  if (customLower !== null && (isNaN(customLower) || customLower < 0 || customLower > 100)) throw new Error('Custom Lower Intensity must be between 0 and 100.');
  if (customUpper !== null && (isNaN(customUpper) || customUpper < 0 || customUpper > 100)) throw new Error('Custom Upper Intensity must be between 0 and 100.');
  if (customLower !== null && customUpper !== null && customLower >= customUpper) throw new Error('Custom Lower Intensity must be less than Custom Upper Intensity.');

  const hrr = mhr - rhr;

  let lowerPct, upperPct;

  // Determine percentages based on training goal or use custom values
  if (customLower !== null && customUpper !== null) {
    lowerPct = customLower / 100;
    upperPct = customUpper / 100;
  } else {
    switch (goal) {
      case 'fat burning':
        lowerPct = 0.50; // 50% HRR
        upperPct = 0.60; // 60% HRR
        break;
      case 'aerobic endurance':
      case 'aerobic':
        lowerPct = 0.60; // 60% HRR
        upperPct = 0.70; // 70% HRR
        break;
      case 'anaerobic threshold':
      case 'threshold':
        lowerPct = 0.70; // 70% HRR
        upperPct = 0.80; // 80% HRR
        break;
      case 'vo2 max / max effort':
      case 'vo2 max':
      case 'max effort':
        lowerPct = 0.80; // 80% HRR
        upperPct = 0.90; // 90% HRR
        break;
      default:
        throw new Error('Invalid Training Goal. Please choose from: Fat Burning, Aerobic Endurance, Anaerobic Threshold, VO2 Max / Max Effort, or provide custom percentages.');
    }
  }

  // Karvonen Formula: Target HR = ((Max HR - Rest HR) * %intensity) + Rest HR
  const lowerHeartRateZone = Math.round((hrr * lowerPct) + rhr);
  const upperHeartRateZone = Math.round((hrr * upperPct) + rhr);

  return {
    lowerHeartRateZone: lowerHeartRateZone,
    upperHeartRateZone: upperHeartRateZone,
    heartRateReserve: hrr
  };
}
