function calculateROI(inputs) {
  const {
    initialInvestment,
    annualCostExistingSources,
    annualCostDiversifiedSources,
    geopoliticalRiskReductionFactor,
    projectLifetime,
    discountRate,
    annualEnergyPriceVolatilityImpact,
    annualMaintenanceCostChange
  } = inputs;

  // Input validation and edge case handling
  if (initialInvestment <= 0) {
    return {
      npv: 0,
      roi: Infinity,
      paybackPeriod: 0,
      totalAnnualBenefit: 0
    };
  }

  const actualDiscountRate = discountRate / 100;

  // Step 1: Calculate Annual Operational Savings
  const annualOperationalSavings = annualCostExistingSources - annualCostDiversifiedSources - annualMaintenanceCostChange;

  // Step 2: Calculate Annual Geopolitical Risk Reduction Value
  const geopoliticalRiskValue = annualCostExistingSources * (geopoliticalRiskReductionFactor / 100);

  // Step 3: Calculate Total Annual Financial Benefit
  const totalAnnualBenefit = annualOperationalSavings + geopoliticalRiskValue + annualEnergyPriceVolatilityImpact;

  let npv = -initialInvestment;
  let cumulativeDiscountedCashFlow = 0;
  let paybackPeriod = Infinity;
  let previousCumulativeDCF = -initialInvestment;
  let previousYear = 0;

  for (let year = 1; year <= projectLifetime; year++) {
    const discountedBenefit = totalAnnualBenefit / Math.pow((1 + actualDiscountRate), year);
    npv += discountedBenefit;
    cumulativeDiscountedCashFlow += discountedBenefit;

    if (paybackPeriod === Infinity && (cumulativeDiscountedCashFlow >= initialInvestment)) {
      // Linear interpolation for payback period
      paybackPeriod = previousYear + (initialInvestment - previousCumulativeDCF) / (cumulativeDiscountedCashFlow - previousCumulativeDCF);
    }
    previousCumulativeDCF = cumulativeDiscountedCashFlow;
    previousYear = year;
  }

  // Step 5: Calculate ROI
  const roi = (npv / initialInvestment) * 100;

  return {
    npv: npv,
    roi: isFinite(roi) ? roi : 0, // Handle division by zero for initialInvestment=0 gracefully
    paybackPeriod: paybackPeriod,
    totalAnnualBenefit: totalAnnualBenefit
  };
}
