const massFlowRateAir = inputs.mass_flow_rate_air;
const inletEnthalpyAir = inputs.inlet_enthalpy_air;
const outletEnthalpyAir = inputs.outlet_enthalpy_air;
let coilEfficiencyFactor = inputs.coil_efficiency_factor;

// Ensure efficiency factor is between 0 and 1
coilEfficiencyFactor = Math.max(0, Math.min(1, coilEfficiencyFactor));

// Ensure mass flow rate is non-negative
const safeMassFlowRateAir = Math.max(0, massFlowRateAir);

// Calculate enthalpy change in kJ/kg
const enthalpyChangeAir = inletEnthalpyAir - outletEnthalpyAir;

// Calculate total coil load (heat transfer rate) in kW
// Q = m * Δh * η
const totalCoilLoadKW = safeMassFlowRateAir * enthalpyChangeAir * coilEfficiencyFactor;

// Convert kW to BTU/hr (1 kW ≈ 3412.14 BTU/hr)
const totalCoilLoadBTUH = totalCoilLoadKW * 3412.14;

return {
    enthalpy_change_air: enthalpyChangeAir,
    total_coil_load_kw: totalCoilLoadKW,
    total_coil_load_btuh: totalCoilLoadBTUH
};
