const density_contribution = Math.min(2, population_density / 1000); // Max 2 points
const sanitation_contribution = Math.min(3, (100 - sanitation_access_percentage) / 20); // Max 3 points
const rainfall_contribution = Math.min(2, annual_rainfall_mm / 1500); // Max 2 points
const healthcare_contribution = Math.min(1.5, (10 - healthcare_access_score) / 5); // Max 1.5 points
const temp_contribution = Math.min(1.5, temperature_celsius / 20); // Max 1.5 points
const water_quality_contribution = Math.min(3, (10 - water_source_quality_score) / 3); // Max 3 points

let overall_risk = 1 + density_contribution + sanitation_contribution + rainfall_contribution + healthcare_contribution + temp_contribution + water_quality_contribution;
overall_risk = Math.min(10, Math.max(1, overall_risk)); // Cap between 1 and 10

// Waterborne Disease Likelihood Calculation (weighted index)
let waterborne_index = 0;
// Sanitation factor: higher for lower sanitation access
waterborne_index += (100 - sanitation_access_percentage) * 0.15; // Max 15 points
// Rainfall factor: higher for more rain (aids contamination, flooding)
waterborne_index += Math.min(10, annual_rainfall_mm / 200); // Max 10 points
// Population density factor: higher for denser populations (easier spread)
waterborne_index += Math.min(5, population_density / 1000); // Max 5 points
// Water quality factor: higher for poorer water quality
waterborne_index += (10 - water_source_quality_score) * 1.0; // Max 9 points
// Temperature factor: higher for warmer temperatures (aids pathogen growth)
waterborne_index += Math.min(5, temperature_celsius / 10); // Max 5 points

let waterborne_disease_likelihood;
if (waterborne_index < 15) {
    waterborne_disease_likelihood = "Low";
} else if (waterborne_index < 25) {
    waterborne_disease_likelihood = "Moderate";
} else if (waterborne_index < 35) {
    waterborne_disease_likelihood = "High";
} else {
    waterborne_disease_likelihood = "Very High";
}

return {
    overall_risk_score: parseFloat(overall_risk.toFixed(1)),
    waterborne_disease_likelihood: waterborne_disease_likelihood,
};
