const ensureNumeric = (value, defaultValue = 0) => typeof value === 'number' && !isNaN(value) && isFinite(value) ? value : defaultValue;
const ensureString = (value, defaultValue = '') => typeof value === 'string' ? value : defaultValue;

const defaultOutput = {
    exposure_score: 0,
    air_pollution_risk: "Low",
    allergen_risk: "Low",
    local_hazard_risk: "Low"
};

const locationScores = {
    'Rural': 1,
    'Suburban': 2,
    'Urban': 3
};

const commuteMethodScores = {
    'Work from Home': 1,
    'Cycling/Walking': 2,
    'Public Transport': 3,
    'Car': 4
};

const indoorAirQualityScores = {
    'Good (HEPA, ventilation)': 1,
    'Moderate (some ventilation, no major sources)': 3,
    'Poor (smoking, minimal ventilation)': 5
};

const dietaryPatternScores = {
    'Organic-focused': 1,
    'Mixed': 3,
    'Conventional-focused': 5
};

const waterSupplyScores = {
    'Filtered Tap Water': 1,
    'Municipal Tap Water (unfiltered)': 2,
    'Tested Well Water': 3,
    'Untested Well Water': 5
};

const industrialProximityScores = {
    '>5 km': 1,
    '1-5 km': 3,
    '<1 km': 5
};

// --- Scoring Logic for Inputs ---
let scoreLocation = locationScores[ensureString(inputs.location_type, 'Rural')] || 1;
let scoreProximity = industrialProximityScores[ensureString(inputs.proximity_to_industrial, '>5 km')] || 1;
let scoreIndoorAir = indoorAirQualityScores[ensureString(inputs.indoor_air_quality_practices, 'Moderate (some ventilation, no major sources)')] || 3;
let scoreDiet = dietaryPatternScores[ensureString(inputs.dietary_pattern, 'Mixed')] || 3;
let scoreWater = waterSupplyScores[ensureString(inputs.water_supply_type, 'Municipal Tap Water (unfiltered)')] || 2;

// AQI Score (clamped between 0 and 500 for practical purposes)
let aqi = Math.max(0, Math.min(500, ensureNumeric(inputs.aqi_residence_average, 50)));
let scoreAQI;
if (aqi <= 50) scoreAQI = 1; // Good
else if (aqi <= 100) scoreAQI = 2; // Moderate
else if (aqi <= 150) scoreAQI = 3; // Unhealthy for Sensitive Groups
else if (aqi <= 200) scoreAQI = 4; // Unhealthy
else scoreAQI = 5; // Very Unhealthy / Hazardous

// Commute Score factors duration and method
let commuteMethod = ensureString(inputs.commute_method, 'Car');
let scoreCommuteMethod = commuteMethodScores[commuteMethod] || 4;
let commuteDurationHours = Math.max(0, Math.min(10, ensureNumeric(inputs.commute_duration_daily, 0))); 
let scoreCommute = scoreCommuteMethod * (commuteDurationHours / 1.5); // Factor daily exposure: e.g., 1.5 hours in car (score 4) gives ~4 points

// Outdoor Activity Score (more activity = more exposure to outdoor elements/pollutants)
let outdoorActivityHoursWeekly = Math.max(0, Math.min(100, ensureNumeric(inputs.outdoor_activity_hours_weekly, 7))); 
let scoreOutdoorActivity = outdoorActivityHoursWeekly / 8; // Every 8 hours outdoor adds 1 point to exposure baseline

// Cumulative Factor (duration of residence - minimum 1 year)
let totalResidenceYears = Math.max(1, ensureNumeric(inputs.total_residence_years, 1)); 

// --- Weighted Component Exposures ---
// Air Pollution Base: Location + AQI + Indoor Air + Commute exposure + Outdoor activity
let airPollutionBaseScore = (scoreLocation * 0.7) + (scoreAQI * 1.8) + (scoreIndoorAir * 1.3) + (scoreCommute * 1.1) + (scoreOutdoorActivity * 0.6);

// Allergen Base: Location + Indoor Air + Outdoor Activity (reflects exposure to pollen, mold, etc.)
let allergenBaseScore = (scoreLocation * 0.4) + (scoreIndoorAir * 1.2) + (scoreOutdoorActivity * 1.6);

// Local Hazard Base: Proximity to Industrial + Water Supply (as local contaminant proxy)
let localHazardBaseScore = (scoreProximity * 2.0) + (scoreWater * 1.5);

// Dietary Hazard Base: Dietary Pattern (reflects pesticide exposure, food contaminants)
let dietaryHazardBaseScore = scoreDiet * 1.7;

// Total Initial Score (sum of weighted components)
let totalInitialScore = airPollutionBaseScore + allergenBaseScore + localHazardBaseScore + dietaryHazardBaseScore;

// Apply cumulative factor (scale by years, using 10 years as a baseline multiplier for relative impact)
let exposure_score = totalInitialScore * (totalResidenceYears / 10);

// Cap exposure score to prevent unrealistic highs or lows (arbitrary range for interpretability)
exposure_score = Math.max(5, Math.min(300, exposure_score)); // Min 5 to indicate some baseline exposure, max 300 for reasonable scale

// Risk Levels for components (mapping based on raw component scores before cumulative factor)
const getRiskLevel = (score, highThreshold, moderateThreshold) => {
    if (score >= highThreshold) return "High";
    if (score >= moderateThreshold) return "Moderate";
    return "Low";
};

let air_pollution_risk_score_raw = (airPollutionBaseScore + scoreAQI + scoreCommute) / 3.0;
let air_pollution_risk = getRiskLevel(air_pollution_risk_score_raw, 15, 8); // Thresholds adjusted for relative scoring

let allergen_risk_score_raw = (allergenBaseScore + scoreIndoorAir + scoreOutdoorActivity) / 3.0;
let allergen_risk = getRiskLevel(allergen_risk_score_raw, 12, 6); // Thresholds adjusted

let local_hazard_risk_score_raw = (localHazardBaseScore + scoreProximity + scoreWater) / 3.0;
let local_hazard_risk = getRiskLevel(local_hazard_risk_score_raw, 14, 7); // Thresholds adjusted

return {
    exposure_score: parseFloat(exposure_score.toFixed(1)),
    air_pollution_risk: air_pollution_risk,
    allergen_risk: allergen_risk,
    local_hazard_risk: local_hazard_risk
};
