/**
 * Calculates the Environmental Stressor Health Impact Score based on various environmental factors.
 * @param {object} inputs - An object containing input values from the user.
 * @param {number} inputs.airQualityAQI - Average Air Quality Index (0-500).
 * @param {number} inputs.noiseLevelDB - Average Daily Noise Level (dBA).
 * @param {number} inputs.waterQualityIndex - Local Water Quality Index (0-100, 100 is best).
 * @param {number} inputs.greenSpaceProximityKM - Distance to Nearest Significant Green Space (km).
 * @param {number} inputs.healthyFoodAccessKM - Distance to Nearest Healthy Food Source (km).
 * @param {number} inputs.industrialDensityScore - Local Industrial Presence (1-5).
 * @param {number} inputs.trafficVolumeScore - Local Traffic Volume/Exposure (1-5).
 * @param {number} inputs.housingMaterialQualityScore - Local Housing Material & Maintenance Quality (1-5).
 * @returns {object} An object containing the calculated health impact score and risk category.
 */
function calculateHealthImpact(inputs) {
    // Input validation and clamping
    const aqi = Math.max(0, Math.min(inputs.airQualityAQI, 500));
    const noise = Math.max(30, Math.min(inputs.noiseLevelDB, 120));
    const wqi = Math.max(0, Math.min(inputs.waterQualityIndex, 100));
    const greenSpace = Math.max(0, inputs.greenSpaceProximityKM);
    const foodAccess = Math.max(0, inputs.healthyFoodAccessKM);
    const industrial = Math.max(1, Math.min(inputs.industrialDensityScore, 5));
    const traffic = Math.max(1, Math.min(inputs.trafficVolumeScore, 5));
    const housing = Math.max(1, Math.min(inputs.housingMaterialQualityScore, 5));

    // 1. Normalize each input into a sub-score (0 to 1), where 1 represents highest negative impact.

    // Air Quality Score: AQI 0=0 impact, AQI 300+=1 impact (e.g., Hazardous)
    // Cap AQI at 300 for severe impact, beyond that impact doesn't scale linearly for *this* model
    const airScore = Math.min(aqi / 300, 1);

    // Noise Level Score: 50dBA=0 impact (quiet residential), 80dBA=0.5 impact, 100dBA+=1 impact (very loud)
    const noiseScore = Math.max(0, Math.min((noise - 50) / 50, 1)); // 50dBA baseline, 50dBA range to 1.0

    // Water Quality Score: WQI 100=0 impact, WQI 0=1 impact
    const waterScore = (100 - wqi) / 100;

    // Green Space Proximity Score: 0km=0 impact, 5km+=1 impact
    const greenSpaceScore = Math.min(greenSpace / 5, 1);

    // Healthy Food Access Score: 0km=0 impact, 10km+=1 impact
    const foodAccessScore = Math.min(foodAccess / 10, 1);

    // Industrial Density Score: 1=0 impact, 5=1 impact
    const industrialScore = (industrial - 1) / 4;

    // Traffic Volume Score: 1=0 impact, 5=1 impact
    const trafficScore = (traffic - 1) / 4;

    // Housing Material Quality Score: 5=0 impact, 1=1 impact
    const housingScore = (5 - housing) / 4;

    // 2. Define weights for each stressor. Weights sum to 1.
    const weights = {
        air: 0.25,
        noise: 0.15,
        water: 0.20,
        greenSpace: 0.10,
        foodAccess: 0.10,
        industrial: 0.08,
        traffic: 0.07,
        housing: 0.05
    };

    // 3. Calculate weighted sum of sub-scores.
    let rawImpactScore = 
        (airScore * weights.air) +
        (noiseScore * weights.noise) +
        (waterScore * weights.water) +
        (greenSpaceScore * weights.greenSpace) +
        (foodAccessScore * weights.foodAccess) +
        (industrialScore * weights.industrial) +
        (trafficScore * weights.traffic) +
        (housingScore * weights.housing);

    // Ensure score is within 0-1 range due to potential rounding or edge cases, though normalizations should prevent this.
    rawImpactScore = Math.max(0, Math.min(rawImpactScore, 1));

    // 4. Scale the final score to 0-100.
    const healthImpactScore = rawImpactScore * 100;

    // 5. Determine risk category.
    let riskCategory;
    if (healthImpactScore <= 20) {
        riskCategory = "Low Risk";
    } else if (healthImpactScore <= 40) {
        riskCategory = "Moderate Risk";
    } else if (healthImpactScore <= 60) {
        riskCategory = "High Risk";
    } else if (healthImpactScore <= 80) {
        riskCategory = "Very High Risk";
    } else {
        riskCategory = "Severe Risk";
    }

    return {
        healthImpactScore: healthImpactScore,
        riskCategory: riskCategory
    };
}
