const { initialInvestment, annualCashFlow, projectLife, discountRate, salvageValue } = inputs;

// Input validation
if (initialInvestment < 0) throw new Error("Initial Investment cannot be negative.");
if (projectLife <= 0) throw new Error("Project Life must be greater than zero.");
if (discountRate < 0) throw new Error("Discount Rate cannot be negative.");

const r = discountRate / 100;

let npv;
let annuityFactor;

if (r === 0) {
    // Special case for zero discount rate
    npv = (annualCashFlow * projectLife) + salvageValue - initialInvestment;
    annuityFactor = projectLife;
} else {
    const invOnePlusRToN = Math.pow(1 + r, -projectLife);

    // Present Value of an ordinary annuity (PVA = CF * [1 - (1+r)^-n] / r)
    const pvAnnuity = annualCashFlow * ((1 - invOnePlusRToN) / r);

    // Present Value of a single sum (Salvage Value)
    const pvSalvage = salvageValue * invOnePlusRToN;

    // Net Present Value (NPV = PVA + PV_Salvage - Initial Investment)
    npv = pvAnnuity + pvSalvage - initialInvestment;

    // Annuity Factor (also known as PVIFA - Present Value Interest Factor of an Annuity)
    // AF = [1 - (1+r)^-n] / r
    annuityFactor = (1 - invOnePlusRToN) / r;
}

// Calculate EAA (EAA = NPV / Annuity Factor)
const eaa = npv / annuityFactor;

// Handle potential edge cases for display
if (!isFinite(npv) || isNaN(npv)) {
    throw new Error("Calculation error: NPV is invalid. Check inputs.");
}
if (!isFinite(eaa) || isNaN(eaa)) {
    throw new Error("Calculation error: EAA is invalid. Check inputs.");
}

return { netPresentValue: npv, equivalentAnnualAnnuity: eaa };
