function calculate({
    totalEstateValue,
    outstandingDebtsAndLiabilities,
    specificBequestsAmount,
    executorAndAdminFeesPercentage,
    federalEstateTaxThreshold,
    federalEstateTaxRate,
    stateEstateTaxThreshold,
    stateEstateTaxRate,
    charitableResiduePercentage,
    numberOfResidualBeneficiaries
}) {
    // Ensure all inputs are numbers and handle invalid inputs gracefully
    totalEstateValue = Number(totalEstateValue) || 0;
    outstandingDebtsAndLiabilities = Number(outstandingDebtsAndLiabilities) || 0;
    specificBequestsAmount = Number(specificBequestsAmount) || 0;
    executorAndAdminFeesPercentage = Number(executorAndAdminFeesPercentage) || 0;
    federalEstateTaxThreshold = Number(federalEstateTaxThreshold) || 0;
    federalEstateTaxRate = Number(federalEstateTaxRate) || 0;
    stateEstateTaxThreshold = Number(stateEstateTaxThreshold) || 0;
    stateEstateTaxRate = Number(stateEstateTaxRate) || 0;
    charitableResiduePercentage = Number(charitableResiduePercentage) || 0;
    numberOfResidualBeneficiaries = Math.max(0, Math.floor(Number(numberOfResidualBeneficiaries) || 0)); // Ensure non-negative integer

    let totalFederalEstateTax = 0;
    let totalStateEstateTax = 0;
    let residualEstateForDistribution = 0;
    let charitableResidueShare = 0;
    let individualResidualBeneficiaryShare = 0;
    let netFederalTaxableEstate = 0;
    let netStateTaxableEstate = 0;

    // Step 1: Calculate initial deductions (debts, specific bequests, executor fees)
    const executorFees = totalEstateValue * executorAndAdminFeesPercentage;
    const initialDeductions = outstandingDebtsAndLiabilities + specificBequestsAmount + executorFees;

    const grossEstateMinusDebtsFeesSpecifics = Math.max(0, totalEstateValue - initialDeductions);

    // Step 2: Calculate Federal Estate Tax
    // Taxable estate is generally reduced by allowed deductions (debts, fees, specific bequests)
    netFederalTaxableEstate = Math.max(0, grossEstateMinusDebtsFeesSpecifics);

    if (netFederalTaxableEstate > federalEstateTaxThreshold) {
        totalFederalEstateTax = (netFederalTaxableEstate - federalEstateTaxThreshold) * federalEstateTaxRate;
    }
    totalFederalEstateTax = Math.max(0, totalFederalEstateTax); // Cannot be negative

    // Step 3: Calculate State Estate Tax
    // State tax calculation often uses a similar base as federal, or state-specific deductions.
    netStateTaxableEstate = Math.max(0, grossEstateMinusDebtsFeesSpecifics); 

    if (netStateTaxableEstate > stateEstateTaxThreshold) {
        totalStateEstateTax = (netStateTaxableEstate - stateEstateTaxThreshold) * stateEstateTaxRate;
    }
    totalStateEstateTax = Math.max(0, totalStateEstateTax); // Cannot be negative

    // Step 4: Calculate total taxes and overall costs
    const totalTaxesAndFees = executorFees + outstandingDebtsAndLiabilities + totalFederalEstateTax + totalStateEstateTax;

    // Step 5: Determine the residual estate for distribution to beneficiaries and charity
    residualEstateForDistribution = Math.max(0, totalEstateValue - totalTaxesAndFees - specificBequestsAmount);

    // Step 6: Distribute residual estate to charity and individual beneficiaries
    charitableResidueShare = residualEstateForDistribution * charitableResiduePercentage;
    const remainingForIndividualBeneficiaries = residualEstateForDistribution - charitableResidueShare;

    if (numberOfResidualBeneficiaries > 0) {
        individualResidualBeneficiaryShare = remainingForIndividualBeneficiaries / numberOfResidualBeneficiaries;
    } else {
        // If no individual residual beneficiaries, the remaining portion after charity goes nowhere here.
        // In reality, it would likely go to the charity if charitableResiduePercentage is 1, or be subject to intestacy.
        individualResidualBeneficiaryShare = 0;
    }

    // Ensure no negative distributions
    charitableResidueShare = Math.max(0, charitableResidueShare);
    individualResidualBeneficiaryShare = Math.max(0, individualResidualBeneficiaryShare);

    return {
        grossEstateMinusDebtsFeesSpecifics: grossEstateMinusDebtsFeesSpecifics,
        netFederalTaxableEstate: netFederalTaxableEstate,
        totalFederalEstateTax: totalFederalEstateTax,
        netStateTaxableEstate: netStateTaxableEstate,
        totalStateEstateTax: totalStateEstateTax,
        totalTaxesAndFees: totalTaxesAndFees,
        residualEstateForDistribution: residualEstateForDistribution,
        charitableResidueShare: charitableResidueShare,
        individualResidualBeneficiaryShare: individualResidualBeneficiaryShare
    };
}
