const totalDeductions = maritalDeduction + charitableDeduction;
const preliminaryTaxableEstate = grossEstateValue - totalDeductions;

// Federal Calculation
const federalTaxableEstate = Math.max(0, preliminaryTaxableEstate - federalExemption + priorTaxableGifts);
const estimatedFederalEstateTax = Math.max(0, federalTaxableEstate * estimatedFederalRate);

// State Calculation (assuming state tax applies similarly but with its own exemption)
const stateTaxableEstate = Math.max(0, preliminaryTaxableEstate - stateExemption);
const estimatedStateEstateTax = Math.max(0, stateTaxableEstate * estimatedStateRate);

const totalEstateTax = estimatedFederalEstateTax + estimatedStateEstateTax;
const netEstateAfterTaxes = grossEstateValue - totalEstateTax;

return {
  federalTaxableEstate: federalTaxableEstate,
  estimatedFederalEstateTax: estimatedFederalEstateTax,
  stateTaxableEstate: stateTaxableEstate,
  estimatedStateEstateTax: estimatedStateEstateTax,
  totalEstateTax: totalEstateTax,
  netEstateAfterTaxes: netEstateAfterTaxes
};
