function calculate({ monthlyBudget, sriAllocationPercent, charityAllocationPercent, sriAnnualReturnRate, sriEthicalImpactFactor, charityEthicalImpactFactor, sustainableConsumptionEthicalImpactFactor, timeHorizonYears }) {
    // Input validation and normalization
    monthlyBudget = Math.max(0, monthlyBudget || 0);
    sriAllocationPercent = Math.max(0, Math.min(100, sriAllocationPercent || 0));
    charityAllocationPercent = Math.max(0, Math.min(100, charityAllocationPercent || 0));
    sriAnnualReturnRate = Math.max(0, sriAnnualReturnRate || 0) / 100; // Convert to decimal
    sriEthicalImpactFactor = Math.max(0.1, sriEthicalImpactFactor || 1);
    charityEthicalImpactFactor = Math.max(0.1, charityEthicalImpactFactor || 1);
    sustainableConsumptionEthicalImpactFactor = Math.max(0.1, sustainableConsumptionEthicalImpactFactor || 1);
    timeHorizonYears = Math.max(1, Math.min(50, timeHorizonYears || 1)); // Cap years for performance/realism

    const annualBudget = monthlyBudget * 12;

    // Calculate sustainable consumption allocation. If SRI + Charity exceed 100%, sustainable consumption is 0.
    let sustainableConsumptionAllocationPercent = 100 - (sriAllocationPercent + charityAllocationPercent);
    if (sustainableConsumptionAllocationPercent < 0) {
        sustainableConsumptionAllocationPercent = 0; 
    }

    // Convert percentages to decimals for calculations
    const sriAlloc = sriAllocationPercent / 100;
    const charityAlloc = charityAllocationPercent / 100;
    const sustainableConsAlloc = sustainableConsumptionAllocationPercent / 100;

    let finalSRICapital = 0;
    let finalPrincipalContributedToSRI = 0;
    let finalCharitableDonations = 0;
    let finalEthicalImpactSRI = 0;
    let finalEthicalImpactCharity = 0;
    let finalEthicalImpactSustainableConsumption = 0;

    const monthlyReturnRate = Math.pow(1 + sriAnnualReturnRate, 1/12) - 1; // Monthly compounding equivalent

    for (let year = 1; year <= timeHorizonYears; year++) {
        const annualSRIContribution = annualBudget * sriAlloc;
        const annualCharityContribution = annualBudget * charityAlloc;
        const annualSustainableConsumption = annualBudget * sustainableConsAlloc;

        // SRI Calculation (compounding monthly)
        finalPrincipalContributedToSRI += annualSRIContribution;
        for (let month = 0; month < 12; month++) {
            finalSRICapital += (annualSRIContribution / 12); // Add monthly contribution
            finalSRICapital *= (1 + monthlyReturnRate); // Apply monthly return
        }

        // Charitable Donations
        finalCharitableDonations += annualCharityContribution;

        // Ethical Impacts (calculated based on annual spending/investing)
        finalEthicalImpactSRI += (annualSRIContribution * sriEthicalImpactFactor);
        finalEthicalImpactCharity += (annualCharityContribution * charityEthicalImpactFactor);
        finalEthicalImpactSustainableConsumption += (annualSustainableConsumption * sustainableConsumptionEthicalImpactFactor);
    }

    const totalFinancialReturn = finalSRICapital - finalPrincipalContributedToSRI;

    // Overall Ethical Impact Score - a weighted sum. Current factors are multipliers on the amount spent/invested.
    const overallEthicalImpactScore = finalEthicalImpactSRI + finalEthicalImpactCharity + finalEthicalImpactSustainableConsumption;

    return {
        totalEthicalCapitalAccumulated: finalSRICapital,
        totalFinancialReturn: totalFinancialReturn > 0 ? totalFinancialReturn : 0, // Ensure non-negative return
        totalCharitableDonations: finalCharitableDonations,
        totalSustainableConsumptionImpact: finalEthicalImpactSustainableConsumption,
        overallEthicalImpactScore: overallEthicalImpactScore
    };
}
