const estimatedVenueCosts = inputs.estimated_venue_costs || 0;
const estimatedPerformerFees = inputs.estimated_performer_fees || 0;
const estimatedMarketingSpend = inputs.estimated_marketing_spend || 0;
const projectedTicketRevenue = inputs.projected_ticket_revenue || 0;
const projectedSponsorshipRevenueLost = inputs.projected_sponsorship_revenue_lost || 0;
const cancellationPenaltyRate = inputs.cancellation_penalty_rate || 0;
const insuranceReimbursementRate = inputs.insurance_reimbursement_rate || 0;

// Calculate core fixed costs that are non-recoverable
const totalFixedCosts = estimatedVenueCosts + estimatedPerformerFees + estimatedMarketingSpend;

// Calculate total lost revenue
const totalLostRevenue = projectedTicketRevenue + projectedSponsorshipRevenueLost;

// Calculate contractual penalties based on fixed costs
const penaltyAmount = totalFixedCosts * (cancellationPenaltyRate / 100);

// Total Gross Financial Loss before any insurance claims or recoveries
const totalGrossFinancialLoss = totalFixedCosts + totalLostRevenue + penaltyAmount;

// Calculate insurance reimbursement
// Assuming insurance covers a percentage of the total gross loss, up to the total loss amount
const insuranceReimbursement = totalGrossFinancialLoss * (insuranceReimbursementRate / 100);

// Ensure insurance payout does not exceed the total gross loss
const effectiveInsuranceReimbursement = Math.min(insuranceReimbursement, totalGrossFinancialLoss);

// Net Financial Impact after accounting for insurance reimbursement
const netFinancialImpact = totalGrossFinancialLoss - effectiveInsuranceReimbursement;

return {
  total_gross_financial_loss: totalGrossFinancialLoss,
  net_financial_impact: netFinancialImpact
};
