const travelEmissionFactor_kg_per_km_per_attendee = 0.18; // Average for mixed travel (e.g., blend of car, train, short flight)
const electricityEmissionFactor_kg_per_kWh = 0.35; // Global average for grid electricity
const wasteEmissionFactor_kg_per_kg = 0.4; // Average for mixed general waste

const travelEmissions_kg = inputs.numAttendees * inputs.avgTravelDistanceKm * 2 * travelEmissionFactor_kg_per_km_per_attendee;
const venueEnergyEmissions_kg = inputs.venueEnergyKWhDay * inputs.eventDurationDays * electricityEmissionFactor_kg_per_kWh;
const wasteEmissions_kg = inputs.wasteGeneratedKgDay * inputs.eventDurationDays * wasteEmissionFactor_kg_per_kg;

const totalCarbonEmissions_kg = travelEmissions_kg + venueEnergyEmissions_kg + wasteEmissions_kg;

const totalCarbonEmissions = totalCarbonEmissions_kg / 1000;

const totalOffsetCost = totalCarbonEmissions * inputs.offsetCostPerTon;

return { totalCarbonEmissions, totalOffsetCost };
