const materialStrengthIndex = parseFloat(inputs.materialStrengthIndex);
const barrierHeightMeters = parseFloat(inputs.barrierHeightMeters);
const deploymentDensityPerMeter = parseFloat(inputs.deploymentDensityPerMeter);
const reinforcementLevel = parseFloat(inputs.reinforcementLevel);
const securityResponseMinutes = parseFloat(inputs.securityResponseMinutes);
const threatLevelIndex = parseFloat(inputs.threatLevelIndex);
const crowdPressureRisk = parseFloat(inputs.crowdPressureRisk);

// Calculate Effectiveness Score
// Base effectiveness is a weighted sum of physical attributes
let baseEffectiveness = (materialStrengthIndex * 7) + 
                        (barrierHeightMeters * 12) + 
                        (deploymentDensityPerMeter * 28) + 
                        (reinforcementLevel * 18);

// Cap effectiveness between 10 and 100
const effectivenessScore = Math.min(100, Math.max(10, baseEffectiveness));

// Calculate Vulnerability Index
// Start with the inverse of effectiveness, then adjust for external factors
let potentialVulnerability = (100 - effectivenessScore);

// Adjust vulnerability based on threat level (higher threat increases vulnerability)
// Threat level 1 means base vulnerability, Threat level 5 means max vulnerability from this factor
let vulnerabilityAdjustedByThreat = potentialVulnerability * (threatLevelIndex / 5);

// Adjust vulnerability based on crowd pressure risk (higher pressure increases vulnerability)
// This adds an additional multiplier from 1x to 2x based on crowd pressure
let vulnerabilityAdjustedByCrowd = vulnerabilityAdjustedByThreat * (1 + (crowdPressureRisk - 1) / 5);

// Add direct impact of security response time (longer time increases vulnerability)
let finalVulnerability = vulnerabilityAdjustedByCrowd + (securityResponseMinutes * 0.75);

// Cap vulnerability between 0 and 100
const vulnerabilityIndex = Math.min(100, Math.max(0, finalVulnerability));

return {
  effectivenessScore: effectivenessScore,
  vulnerabilityIndex: vulnerabilityIndex
};
