function calculateSecurityBudget(inputs) {
  const expected_attendance = Math.max(1000, inputs.expected_attendance || 0);
  const event_duration_hours = Math.max(4, inputs.event_duration_hours || 0);
  const security_staff_hourly_rate = Math.max(0, inputs.security_staff_hourly_rate || 0);
  const k9_units_count = Math.max(0, inputs.k9_units_count || 0);
  const medical_staff_hourly_rate = Math.max(0, inputs.medical_staff_hourly_rate || 0);
  const perimeter_length_meters = Math.max(0, inputs.perimeter_length_meters || 0);
  const cctv_cameras_count = Math.max(0, inputs.cctv_cameras_count || 0);
  const venue_type = inputs.venue_type;
  const threat_assessment_level = inputs.threat_assessment_level;
  const emergency_comm_system_level = inputs.emergency_comm_system_level;

  // --- Constants ---
  const BASE_SECURITY_STAFF_RATIO_PER_1000 = 4; // 1 security staff per 250 attendees
  const BASE_MEDICAL_STAFF_RATIO_PER_1000 = 2; // 1 medical staff per 500 attendees
  const PERIMETER_FENCING_COST_PER_METER = 25; // Rental + setup for event duration
  const K9_UNIT_HOURLY_COST_PER_UNIT = 150; // Includes handler, dog, vehicle, per hour
  const CCTV_CAMERA_BASE_COST = 800; // Includes rental, setup, monitoring for event duration
  const EMERGENCY_COMM_BASIC_COST = 5000; // Base cost for basic comm system
  const EMERGENCY_COMM_ADVANCED_COST = 20000; // Base cost for advanced comm system
  const CONTINGENCY_PERCENTAGE = 0.15; // 15% contingency

  const THREAT_MULTIPLIERS = {
    "Low": 1.0,
    "Moderate": 1.2,
    "Elevated": 1.5,
    "High": 2.0,
    "Severe": 2.5
  };

  const VENUE_MULTIPLIERS = {
    "Urban Open-Air": 1.3,
    "Stadium/Arena": 1.0,
    "Indoor Convention Center": 0.9,
    "Multi-Site Campus": 1.5
  };

  // --- Input Validation and Defaulting ---
  const threatMultiplier = THREAT_MULTIPLIERS[threat_assessment_level] || 1.0;
  const venueMultiplier = VENUE_MULTIPLIERS[venue_type] || 1.0;

  // --- Calculation Steps ---
  // 1. Calculate Staffing Costs
  const adjustedSecurityStaffRatio = BASE_SECURITY_STAFF_RATIO_PER_1000 * threatMultiplier * venueMultiplier;
  const requiredSecurityStaff = Math.ceil((expected_attendance / 1000) * adjustedSecurityStaffRatio);
  const securityStaffCost = requiredSecurityStaff * security_staff_hourly_rate * event_duration_hours;

  const adjustedMedicalStaffRatio = BASE_MEDICAL_STAFF_RATIO_PER_1000 * threatMultiplier;
  const requiredMedicalStaff = Math.ceil((expected_attendance / 1000) * adjustedMedicalStaffRatio);
  const medicalStaffCost = requiredMedicalStaff * medical_staff_hourly_rate * event_duration_hours;

  const totalStaffingCost = securityStaffCost + medicalStaffCost;

  // 2. Calculate Specialized Unit & Equipment Costs
  const k9UnitCost = k9_units_count * K9_UNIT_HOURLY_COST_PER_UNIT * event_duration_hours;
  const perimeterFencingCost = perimeter_length_meters * PERIMETER_FENCING_COST_PER_METER;
  const cctvCameraCost = cctv_cameras_count * CCTV_CAMERA_BASE_COST;

  let emergencyCommCost = 0;
  if (emergency_comm_system_level === "Advanced") {
    emergencyCommCost = EMERGENCY_COMM_ADVANCED_COST;
  } else {
    emergencyCommCost = EMERGENCY_COMM_BASIC_COST;
  }

  const totalEquipmentCost = k9UnitCost + perimeterFencingCost + cctvCameraCost + emergencyCommCost;

  // 3. Sum sub-totals
  const subtotalCost = totalStaffingCost + totalEquipmentCost;

  // 4. Add Contingency
  const contingencyCost = subtotalCost * CONTINGENCY_PERCENTAGE;
  const totalSecurityCost = subtotalCost + contingencyCost;

  return {
    staffing_cost: totalStaffingCost,
    equipment_cost: totalEquipmentCost,
    contingency_cost: contingencyCost,
    total_security_cost: totalSecurityCost
  };
}
