function calculate() {
    const currentCoL = parseFloat(inputs.currentCityCostOfLivingIndex);
    const destinationCoL = parseFloat(inputs.destinationCityCostOfLivingIndex);
    const currentIncome = parseFloat(inputs.currentAnnualGrossIncome);
    let familySize = parseInt(inputs.familySize);
    const relocationDistance = parseFloat(inputs.estimatedRelocationDistanceKm);
    const shippingVolume = parseFloat(inputs.estimatedShippingVolumeCubicMeters);
    const visaFeePerPerson = parseFloat(inputs.averageVisaApplicationFeePerPerson);
    const tempAccWeeks = parseFloat(inputs.temporaryAccommodationWeeks);
    const taxDifferential = parseFloat(inputs.estimatedIncomeTaxDifferentialPercent);

    // Input validation and default values
    if (isNaN(currentCoL) || currentCoL <= 0) return { error: 'Invalid Current City CoL Index.' };
    if (isNaN(destinationCoL) || destinationCoL <= 0) return { error: 'Invalid Destination City CoL Index.' };
    if (isNaN(currentIncome) || currentIncome <= 0) return { error: 'Invalid Current Annual Gross Income.' };
    if (isNaN(familySize) || familySize < 1) familySize = 1; // Ensure family size is at least 1
    if (isNaN(relocationDistance) || relocationDistance < 0) return { error: 'Invalid Relocation Distance.' };
    if (isNaN(shippingVolume) || shippingVolume < 0) return { error: 'Invalid Shipping Volume.' };
    if (isNaN(visaFeePerPerson) || visaFeePerPerson < 0) return { error: 'Invalid Visa Fee.' };
    if (isNaN(tempAccWeeks) || tempAccWeeks < 0) return { error: 'Invalid Temporary Accommodation Weeks.' };
    if (isNaN(taxDifferential)) return { error: 'Invalid Tax Differential.' };

    // Step 1: Calculate Cost of Living Differential
    const costOfLivingDifferentialFactor = destinationCoL / currentCoL;
    const costOfLivingDifferentialPercent = (costOfLivingDifferentialFactor - 1) * 100;

    // Step 2: Calculate Adjusted Annual Income Needed
    // This is the income required to maintain the *same purchasing power* for goods and services, before tax adjustment.
    const baseAdjustedIncome = currentIncome * costOfLivingDifferentialFactor;

    // Adjust for tax differential. If taxDifferential is -5%, it means taxes are lower, so income can be less for same net. 
    // If taxDifferential is +10%, taxes are higher, so income needs to be more.
    const taxFactor = 1 + (taxDifferential / 100); // e.g., if taxDiff is -5%, factor is 0.95. If +10%, factor is 1.10.
    const adjustedAnnualIncomeNeeded = baseAdjustedIncome * taxFactor;

    // Step 3: Estimate Monthly Housing Cost
    // A common rule of thumb is 30% of gross income for housing. We'll adjust this by CoL factor and family size.
    // We assume housing costs scale somewhat with CoL and moderately with family size.
    const baseMonthlyHousing = (currentIncome * 0.30) / 12; // 30% of current income for housing
    const estimatedMonthlyHousingCost = baseMonthlyHousing * costOfLivingDifferentialFactor * (1 + (familySize - 1) * 0.05); // +5% per additional family member for housing needs

    // Step 4: Calculate Total Estimated Relocation Expenses
    // a. Shipping Costs: A rough estimate based on volume and distance. 
    //    Assume a base cost per cubic meter per 1000km, plus a fixed handling fee.
    const shippingCostPerCubicMeterPer1000Km = 150; // Example: $150 per m^3 per 1000km
    const baseShippingFixedCost = 750; // A base handling fee
    const estimatedShippingCost = (shippingVolume * (relocationDistance / 1000) * shippingCostPerCubicMeterPer1000Km) + baseShippingFixedCost;

    // b. Visa/Immigration Fees:
    const totalVisaFees = visaFeePerPerson * familySize;

    // c. Temporary Accommodation Costs: Assume a daily rate per person for temp housing.
    const dailyTempAccCostPerPerson = 75; // Example: $75 per person per day
    const totalTemporaryAccommodationCost = tempAccWeeks * 7 * dailyTempAccCostPerPerson * familySize;

    // d. Contingency/Buffer: Essential for unexpected costs.
    const contingencyFactor = 0.15; // 15% of initial expenses as buffer
    const initialRelocationExpenses = estimatedShippingCost + totalVisaFees + totalTemporaryAccommodationCost;
    const contingencyAmount = initialRelocationExpenses * contingencyFactor;

    const totalEstimatedRelocationExpenses = initialRelocationExpenses + contingencyAmount;

    return {
        adjustedAnnualIncomeNeeded: adjustedAnnualIncomeNeeded,
        estimatedMonthlyHousingCost: estimatedMonthlyHousingCost,
        totalEstimatedRelocationExpenses: totalEstimatedRelocationExpenses,
        costOfLivingDifferentialPercent: costOfLivingDifferentialPercent
    };
}
