function calculateHeatLoss(inputs) {
    const {
        exteriorTemperature,
        desiredInteriorTemperature,
        homeSquareFootage,
        ceilingHeight,
        wallRValue,
        atticRValue,
        windowUFactor,
        airLeakageRate,
        costPer100kBTU_Delivered
    } = inputs;

    // Validate inputs
    if ([exteriorTemperature, desiredInteriorTemperature, homeSquareFootage, ceilingHeight,
        wallRValue, atticRValue, windowUFactor, airLeakageRate, costPer100kBTU_Delivered].some(val => val === null || val === undefined || isNaN(val))) {
        return {
            estimatedDailyHeatLoss: null,
            estimatedDailyHeatingCost: null,
            recommendedHeatingSetpoint: null,
            insulationRecommendation: "Please provide valid inputs for all fields.",
            airSealingRecommendation: "Please provide valid inputs for all fields.",
            annualSavingsPotential: null
        };
    }

    // Constants
    const BTU_PER_CUBIC_FOOT_DEG_F_ACH = 0.018; // approx. BTU required to heat 1 cu ft of air by 1 deg F per ACH
    const TARGET_WALL_R = 20; // Recommended minimum wall R-value for cold climates
    const TARGET_ATTIC_R = 49; // Recommended minimum attic R-value for cold climates (DOE Guideline)
    const TARGET_WINDOW_U = 0.27; // Recommended maximum window U-factor (equivalent to R-3.7, typical for ENERGY STAR® windows)
    const TARGET_AIR_LEAKAGE = 0.35; // Recommended maximum ACH for energy efficient homes
    const AVG_WINDOW_PERCENTAGE_OF_WALL = 0.15; // Assume 15% of wall area is window
    const AVG_ROOF_PITCH_FACTOR = 1.1; // Factor for attic surface area being slightly larger than floor due to pitch
    const SETPOINT_ADJUST_THRESHOLD_TEMP = 10; // Exterior temp below which setpoint adjustment is considered
    const SETPOINT_ADJUST_VALUE = -3; // degrees F to reduce setpoint for optimization
    const AVERAGE_HEATING_DAYS_PER_YEAR = 180; // Used for annual savings estimation

    // Calculate Delta T (Temperature Difference)
    let deltaT = desiredInteriorTemperature - exteriorTemperature;

    if (deltaT <= 0) {
        // If exterior is warmer or equal to interior, no heating is required, or it's a cooling load scenario.
        // We cap deltaT at a minimal positive value to prevent division by zero in some theoretical edge cases where R=infinity.
        // For practical heating calculations, if deltaT <= 0, heat loss is effectively 0 for heating purposes.
        return {
            estimatedDailyHeatLoss: 0,
            estimatedDailyHeatingCost: 0,
            recommendedHeatingSetpoint: desiredInteriorTemperature,
            insulationRecommendation: "No heating load detected. Your home may be overheating or require cooling.",
            airSealingRecommendation: "No heating load detected. Air sealing is always beneficial for comfort and cooling.",
            annualSavingsPotential: 0
        };
    }

    // 1. Calculate Home Volume
    const homeVolume = homeSquareFootage * ceilingHeight;

    // 2. Calculate Surface Areas (simplified assumptions based on square footage)
    // Approximating perimeter for a square house to estimate total wall area.
    const perimeter = Math.sqrt(homeSquareFootage) * 4;
    const totalWallArea = perimeter * ceilingHeight;
    const windowArea = totalWallArea * AVG_WINDOW_PERCENTAGE_OF_WALL;
    const effectiveWallArea = totalWallArea - windowArea;
    const atticArea = homeSquareFootage * AVG_ROOF_PITCH_FACTOR; // Roof area slightly larger than floor footprint

    // Ensure R-values are not zero to prevent division by zero. Use a very large number if R-value is too low.
    const safeWallRValue = Math.max(0.1, wallRValue);
    const safeAtticRValue = Math.max(0.1, atticRValue);

    // 3. Calculate Heat Loss by Conduction (BTU/hr)
    // Heat Loss = (Area / R-value) * DeltaT or Area * U-factor * DeltaT
    const wallHeatLoss = (effectiveWallArea / safeWallRValue) * deltaT;
    const atticHeatLoss = (atticArea / safeAtticRValue) * deltaT;
    const windowHeatLoss = (windowArea * windowUFactor) * deltaT;

    // 4. Calculate Heat Loss by Infiltration (Air Leakage) (BTU/hr)
    // Formula: Volume * ACH * 0.018 * DeltaT
    const infiltrationHeatLoss = homeVolume * airLeakageRate * BTU_PER_CUBIC_FOOT_DEG_F_ACH * deltaT;

    // 5. Total Hourly Heat Loss (BTU/hr)
    const totalHourlyHeatLoss = wallHeatLoss + atticHeatLoss + windowHeatLoss + infiltrationHeatLoss;

    // 6. Total Daily Heat Loss (BTU/day)
    const estimatedDailyHeatLoss = totalHourlyHeatLoss * 24;

    // 7. Estimated Daily Heating Cost ($/day)
    // costPer100kBTU_Delivered directly represents the cost for 100,000 BTU of useful heat.
    const estimatedDailyHeatingCost = (estimatedDailyHeatLoss / 100000) * costPer100kBTU_Delivered;

    // 8. Recommendations and Savings Potential
    let insulationRecommendation = "Your home's current insulation levels are performing well for extreme cold conditions.";
    let airSealingRecommendation = "Your home's air leakage rate is efficient. Maintain this performance through regular checks.";
    let potentialAnnualSavings = 0;

    // Evaluate Wall Insulation
    if (wallRValue < TARGET_WALL_R) {
        const currentLoss = (effectiveWallArea / safeWallRValue) * deltaT;
        const targetLoss = (effectiveWallArea / TARGET_WALL_R) * deltaT;
        const hourlySavings = currentLoss - targetLoss;
        potentialAnnualSavings += hourlySavings * 24 * AVERAGE_HEATING_DAYS_PER_YEAR; // Annual BTU savings
        insulationRecommendation = "Consider upgrading wall insulation. Your current R-value of " + wallRValue + " is below the recommended R-" + TARGET_WALL_R + ".";
    }

    // Evaluate Attic Insulation
    if (atticRValue < TARGET_ATTIC_R) {
        const currentLoss = (atticArea / safeAtticRValue) * deltaT;
        const targetLoss = (atticArea / TARGET_ATTIC_R) * deltaT;
        const hourlySavings = currentLoss - targetLoss;
        potentialAnnualSavings += hourlySavings * 24 * AVERAGE_HEATING_DAYS_PER_YEAR; // Annual BTU savings
        if (insulationRecommendation.includes("Consider upgrading")) {
            insulationRecommendation += " Additionally, attic insulation at R-" + atticRValue + " is less than the recommended R-" + TARGET_ATTIC_R + ".";
        } else {
            insulationRecommendation = "Consider upgrading attic insulation. Your current R-value of " + atticRValue + " is below the recommended R-" + TARGET_ATTIC_R + ".";
        }
    }

    // Evaluate Window Efficiency
    if (windowUFactor > TARGET_WINDOW_U) {
        const currentLoss = (windowArea * windowUFactor) * deltaT;
        const targetLoss = (windowArea * TARGET_WINDOW_U) * deltaT;
        const hourlySavings = currentLoss - targetLoss;
        potentialAnnualSavings += hourlySavings * 24 * AVERAGE_HEATING_DAYS_PER_YEAR; // Annual BTU savings
        if (insulationRecommendation.includes("Consider upgrading")) {
            insulationRecommendation += " Upgrading windows (current U-factor: " + windowUFactor + ") to a more efficient U-factor (e.g., " + TARGET_WINDOW_U + ") could also yield significant savings.";
        } else {
            insulationRecommendation = "Consider upgrading windows. Your current U-factor of " + windowUFactor + " is higher (less efficient) than the recommended U-" + TARGET_WINDOW_U + ".";
        }
    }

    // Evaluate Air Sealing
    if (airLeakageRate > TARGET_AIR_LEAKAGE) {
        const currentLoss = homeVolume * airLeakageRate * BTU_PER_CUBIC_FOOT_DEG_F_ACH * deltaT;
        const targetLoss = homeVolume * TARGET_AIR_LEAKAGE * BTU_PER_CUBIC_FOOT_DEG_F_ACH * deltaT;
        const hourlySavings = currentLoss - targetLoss;
        potentialAnnualSavings += hourlySavings * 24 * AVERAGE_HEATING_DAYS_PER_YEAR; // Annual BTU savings
        airSealingRecommendation = "Strongly recommend professional air sealing. Your air leakage rate of " + airLeakageRate + " ACH is significantly higher than the efficient target of " + TARGET_AIR_LEAKAGE + " ACH.";
    }

    const annualSavingsPotential = (potentialAnnualSavings / 100000) * costPer100kBTU_Delivered;

    // Optimal Setpoint Adjustment: For extreme cold, reducing setpoint slightly can save significant energy.
    let recommendedHeatingSetpoint = desiredInteriorTemperature;
    if (exteriorTemperature < SETPOINT_ADJUST_THRESHOLD_TEMP && desiredInteriorTemperature > (65 + Math.abs(SETPOINT_ADJUST_VALUE))) { // Only recommend reduction if it doesn't go below a reasonable comfort level
        recommendedHeatingSetpoint = desiredInteriorTemperature + SETPOINT_ADJUST_VALUE;
    }

    return {
        estimatedDailyHeatLoss: Math.round(estimatedDailyHeatLoss),
        estimatedDailyHeatingCost: parseFloat(estimatedDailyHeatingCost.toFixed(2)),
        recommendedHeatingSetpoint: Math.round(recommendedHeatingSetpoint),
        insulationRecommendation: insulationRecommendation,
        airSealingRecommendation: airSealingRecommendation,
        annualSavingsPotential: Math.round(annualSavingsPotential)
    };
}
