function calculate({
    home_sq_ft,
    avg_indoor_temp_desired,
    normal_avg_outdoor_temp,
    forecast_avg_outdoor_temp,
    cold_snap_duration_days,
    insulation_level,
    heating_system_efficiency,
    energy_cost_per_unit,
    energy_unit_type
}) {
    // --- Input Validation and Edge Cases ---
    if (home_sq_ft <= 0 || avg_indoor_temp_desired <= 0 || cold_snap_duration_days <= 0 || heating_system_efficiency <= 0 || energy_cost_per_unit <= 0) {
        return {
            estimated_consumption_increase: 0,
            estimated_cost_increase: 0,
            total_estimated_cost_cold_snap: 0,
            unit: "" 
        };
    }

    // Cap efficiency (no more than 100% efficient)
    heating_system_efficiency = Math.min(100, Math.max(1, heating_system_efficiency));

    // --- Constants and Conversions ---
    const BTU_PER_KWH = 3412; // Approx. BTUs in 1 kWh
    const BTU_PER_THERM = 100000; // BTUs in 1 Therm of natural gas
    const BTU_PER_GALLON_PROPANE = 91330; // Approx. BTUs in 1 gallon of liquid propane

    let r_value;
    switch (insulation_level) {
        case "poor": r_value = 10; break;
        case "average": r_value = 20; break;
        case "good": r_value = 30; break;
        case "excellent": r_value = 40; break;
        default: r_value = 20; // Fallback to average
    }
    const u_value = 1 / r_value; // U-value = 1/R-value (BTU/hr·ft²·°F)

    // Calculate Delta T (Temperature Difference for heating)
    // Only consider positive differences as heat loss for heating calculations.
    const delta_t_normal = Math.max(0, avg_indoor_temp_desired - normal_avg_outdoor_temp);
    const delta_t_cold_snap = Math.max(0, avg_indoor_temp_desired - forecast_avg_outdoor_temp);

    // --- Calculate Heat Loss in BTUs ---
    // Heat Loss (BTU/day) = Area (sq ft) * U-value (BTU/hr·ft²·°F) * DeltaT (°F) * Hours per day (24)
    // Total Heat Loss for the period = Daily Heat Loss * Duration (days)

    // Total heat loss under normal conditions for the specified duration
    const total_heat_loss_normal_period_btu = home_sq_ft * u_value * delta_t_normal * 24 * cold_snap_duration_days;

    // Total heat loss during the extreme cold snap for the specified duration
    const total_heat_loss_cold_snap_period_btu = home_sq_ft * u_value * delta_t_cold_snap * 24 * cold_snap_duration_days;

    // Additional heat loss specifically due to extreme cold conditions
    const additional_heat_loss_btu = Math.max(0, total_heat_loss_cold_snap_period_btu - total_heat_loss_normal_period_btu);

    // --- Account for Heating System Efficiency ---
    // Actual energy input needed by the system = Heat Loss (BTU) / (Efficiency / 100)
    const efficiency_factor = heating_system_efficiency / 100;
    const actual_additional_energy_needed_btu = additional_heat_loss_btu / efficiency_factor;
    const actual_total_energy_needed_btu_cold_snap = total_heat_loss_cold_snap_period_btu / efficiency_factor;


    // --- Convert BTUs to specified energy units ---
    let estimated_consumption_increase = 0;
    let total_estimated_consumption_cold_snap = 0;
    let unit = "";
    let btu_conversion_factor = 1; 

    switch (energy_unit_type) {
        case "kwh":
            btu_conversion_factor = BTU_PER_KWH;
            unit = "kWh";
            break;
        case "therms":
            btu_conversion_factor = BTU_PER_THERM;
            unit = "Therms";
            break;
        case "gallons_propane":
            btu_conversion_factor = BTU_PER_GALLON_PROPANE;
            unit = "Gallons";
            break;
        default:
            btu_conversion_factor = BTU_PER_KWH; // Default fallback
            unit = "kWh";
    }

    estimated_consumption_increase = actual_additional_energy_needed_btu / btu_conversion_factor;
    total_estimated_consumption_cold_snap = actual_total_energy_needed_btu_cold_snap / btu_conversion_factor;

    // --- Calculate Costs ---
    const estimated_cost_increase = estimated_consumption_increase * energy_cost_per_unit;
    const total_estimated_cost_cold_snap = total_estimated_consumption_cold_snap * energy_cost_per_unit;

    return {
        estimated_consumption_increase: estimated_consumption_increase,
        estimated_cost_increase: estimated_cost_increase,
        total_estimated_cost_cold_snap: total_estimated_cost_cold_snap,
        unit: unit 
    };
}
