formula:
/**
 * Calculates the readiness and potential risks for individuals undertaking extreme expeditions.
 * Factors in physical fitness, equipment, route difficulty, and environmental conditions.
 */

// Helper functions to map raw input values to a 0-100 score, higher score means better (for positive factors) or worse (for negative factors/hazards)
function mapVo2MaxToScore(vo2) {
    if (vo2 < 30) return 10;
    if (vo2 < 40) return 30;
    if (vo2 < 50) return 60;
    if (vo2 < 60) return 85;
    return 100; // >= 60
}

function mapAltitudeExpToScore(exp) {
    if (exp === 0) return 0;
    if (exp === 1) return 30;
    if (exp === 2) return 65;
    return 100; // >= 3
}

function mapRouteTechDiffToScore(diff) { // Higher score = higher difficulty/risk
    if (diff === 1) return 10;
    if (diff === 2) return 30;
    if (diff === 3) return 60;
    if (diff === 4) return 85;
    return 100; // diff === 5
}

function mapTotalAscentToScore(ascent) { // Higher score = higher difficulty/risk
    if (ascent < 1000) return 10;
    if (ascent < 3000) return 40;
    if (ascent < 5000) return 70;
    if (ascent < 8000) return 90;
    return 100; // >= 8000
}

function mapTemperatureToHazard(temp) { // Lower temp = higher hazard score
    if (temp > 0) return 10;
    if (temp > -10) return 30;
    if (temp > -20) return 60;
    if (temp > -30) return 85;
    return 100; // <= -30
}

function mapWindSpeedToHazard(wind) { // Higher wind = higher hazard score
    if (wind < 30) return 10;
    if (wind < 60) return 40;
    if (wind < 90) return 70;
    if (wind < 120) return 90;
    return 100; // >= 120
}

function mapVolatilityToHazard(volatility) { // Higher volatility = higher hazard score
    if (volatility === 1) return 10;
    if (volatility === 2) return 30;
    if (volatility === 3) return 60;
    if (volatility === 4) return 85;
    return 100; // volatility === 5
}

// Input Validation and Clamping
const vo2Max = Math.max(10, Math.min(80, inputs.vo2Max));
const highAltitudeExperience = Math.max(0, Math.min(10, inputs.highAltitudeExperience));
const equipmentReadinessPercentage = Math.max(0, Math.min(100, inputs.equipmentReadinessPercentage));
const routeTechnicalDifficulty = Math.max(1, Math.min(5, inputs.routeTechnicalDifficulty));
const totalAscentMeters = Math.max(0, Math.min(15000, inputs.totalAscentMeters));
const expectedMinTemperatureCelsius = Math.max(-60, Math.min(40, inputs.expectedMinTemperatureCelsius));
const expectedMaxWindSpeedKmh = Math.max(0, Math.min(200, inputs.expectedMaxWindSpeedKmh));
const weatherVolatilityScore = Math.max(1, Math.min(5, inputs.weatherVolatilityScore));

// 1. Calculate Fitness Score (0-100, higher is better)
const vo2Score = mapVo2MaxToScore(vo2Max);
const altitudeScore = mapAltitudeExpToScore(highAltitudeExperience);
let fitnessScore = (vo2Score * 0.6) + (altitudeScore * 0.4);
fitnessScore = Math.max(0, Math.min(100, fitnessScore));

// 2. Calculate Equipment Score (0-100, higher is better)
let equipmentScore = equipmentReadinessPercentage;
equipmentScore = Math.max(0, Math.min(100, equipmentScore));

// 3. Calculate Route Difficulty Score (0-100, higher is worse/more difficult)
const techDiffScore = mapRouteTechDiffToScore(routeTechnicalDifficulty);
const ascentScore = mapTotalAscentToScore(totalAscentMeters);
let routeDifficultyScore = (techDiffScore * 0.7) + (ascentScore * 0.3);
routeDifficultyScore = Math.max(0, Math.min(100, routeDifficultyScore));

// 4. Calculate Environmental Hazard Index (0-100, higher is worse/more hazardous)
const tempHazard = mapTemperatureToHazard(expectedMinTemperatureCelsius);
const windHazard = mapWindSpeedToHazard(expectedMaxWindSpeedKmh);
const volatilityHazard = mapVolatilityToHazard(weatherVolatilityScore);
let environmentalHazardIndex = (tempHazard * 0.4) + (windHazard * 0.3) + (volatilityHazard * 0.3);
environmentalHazardIndex = Math.max(0, Math.min(100, environmentalHazardIndex));

// 5. Calculate Physical Stress Index (0-100, higher is worse/more stressful)
// This combines lack of fitness with objective route difficulty.
let physicalStressIndex = ((100 - fitnessScore) * 0.6) + (routeDifficultyScore * 0.4);
physicalStressIndex = Math.max(0, Math.min(100, physicalStressIndex));

// 6. Calculate Overall Readiness Score (0-100%, higher is better)
// This balances positive (fitness, equipment) and negative factors (difficulty, environment).
let readinessScore = (fitnessScore * 0.3) + (equipmentScore * 0.3) + 
                     ((100 - routeDifficultyScore) * 0.2) + ((100 - environmentalHazardIndex) * 0.2);
readinessScore = Math.max(0, Math.min(100, readinessScore));

// 7. Determine Overall Risk Score (0-100, higher is worse)
// This combines all negative aspects: physical stress, environmental hazards, and lack of equipment.
let overallRiskScore = (physicalStressIndex * 0.4) + (environmentalHazardIndex * 0.4) + ((100 - equipmentScore) * 0.2);
overallRiskScore = Math.max(0, Math.min(100, overallRiskScore));

// 8. Categorize Risk Level
let riskLevel;
if (overallRiskScore < 25) {
    riskLevel = 'Low';
} else if (overallRiskScore < 50) {
    riskLevel = 'Moderate';
} else if (overallRiskScore < 75) {
    riskLevel = 'High';
} else {
    riskLevel = 'Extreme';
}

return {
    readinessScore: Math.round(readinessScore),
    riskLevel: riskLevel,
    physicalStressIndex: parseFloat(physicalStressIndex.toFixed(1)),
    environmentalHazardIndex: parseFloat(environmentalHazardIndex.toFixed(1))
};
