function calculateResilience(inputs) {
    const {
        weatherEventSeverity,
        overheadLinePercentage,
        averageInfrastructureAgeYears,
        redundancyScore,
        maintenanceScore,
        recoveryResourceScore,
        interconnectivityScore,
        criticalLoadPercentage
    } = inputs;

    // --- Input Validation and Normalization (Cap to reasonable ranges) ---
    const sev = Math.min(10, Math.max(1, weatherEventSeverity));
    const ohPct = Math.min(100, Math.max(0, overheadLinePercentage));
    const age = Math.min(80, Math.max(0, averageInfrastructureAgeYears)); // Cap age at 80 years
    const red = Math.min(10, Math.max(1, redundancyScore));
    const maint = Math.min(10, Math.max(1, maintenanceScore));
    const recRes = Math.min(10, Math.max(1, recoveryResourceScore));
    const intercon = Math.min(10, Math.max(1, interconnectivityScore));
    const critLoad = Math.min(100, Math.max(0, criticalLoadPercentage));

    // --- Step 1: Calculate Base Vulnerability Score (0-70) ---
    // Higher severity leads to higher base vulnerability
    let vulnerability = sev * 7; 

    // --- Step 2: Adjust Vulnerability based on Infrastructure Characteristics ---
    // Infrastructure Age: Older infrastructure significantly increases vulnerability
    if (age > 40) {
        vulnerability += (age - 40) * 0.7; // Significant increase for very old infrastructure
    } else if (age > 20) {
        vulnerability += (age - 20) * 0.3; // Moderate increase for older infrastructure
    }

    // Infrastructure Type: Higher overhead percentage increases vulnerability significantly
    vulnerability += (ohPct / 100) * 25; // Max +25 for 100% overhead lines

    // --- Step 3: Mitigate Vulnerability based on Grid Strengths and Practices ---
    // Redundancy: High redundancy significantly decreases vulnerability
    vulnerability -= (red - 1) * 2.0; // Max -18 for redundancy score 10

    // Maintenance History: Excellent maintenance decreases vulnerability
    vulnerability -= (maint - 1) * 1.5; // Max -13.5 for maintenance score 10

    // Interconnectivity: High interconnectivity decreases vulnerability by allowing power rerouting
    vulnerability -= (intercon - 1) * 1.2; // Max -10.8 for interconnectivity score 10

    // Ensure vulnerability score stays within a logical range [0, 100]
    vulnerability = Math.min(100, Math.max(0, vulnerability));

    // --- Step 4: Estimate Recovery Time (in hours) ---
    // Base recovery time is proportional to vulnerability score. 
    // A score of 0 implies a minimum recovery time (e.g., 6 hours for minor issues),
    // A score of 100 implies a very long recovery time (e.g., 300 hours base).
    let recoveryTime = (vulnerability * 2.5) + 6; // Range from 6 hours (v=0) to 256 hours (v=100)

    // Recovery Resources: Abundant resources significantly decrease recovery time
    recoveryTime -= (recRes - 1) * 15; // Max -135 hours for recovery resource score 10

    // Critical Load: High critical load, while prioritized, can increase the *overall* time 
    // for full grid restoration due to complexity, safety protocols, and meticulous verification.
    recoveryTime += (critLoad / 100) * 40; // Max +40 hours for 100% critical load

    // Ensure recovery time is realistic and positive (min 4 hours, max ~3 weeks or 504 hours)
    recoveryTime = Math.min(504, Math.max(4, recoveryTime)); 

    // --- Step 5: Assign Resilience Rating ---
    let resilienceRating;
    if (vulnerability <= 15) {
        resilienceRating = "A"; // Excellent: Highly resilient, minimal impact, rapid recovery
    } else if (vulnerability <= 30) {
        resilienceRating = "B"; // Good: Resilient, manageable impact, quick recovery
    } else if (vulnerability <= 50) {
        resilienceRating = "C"; // Moderate: Moderate resilience, noticeable impact, moderate recovery
    } else if (vulnerability <= 70) {
        resilienceRating = "D"; // Weak: Low resilience, significant impact, prolonged recovery
    } else if (vulnerability <= 85) {
        resilienceRating = "E"; // Very Weak: Highly vulnerable, severe impact, very prolonged recovery
    } else {
        resilienceRating = "F"; // Critical: Extremely vulnerable, catastrophic impact, extended recovery
    }

    return {
        vulnerabilityScore: Math.round(vulnerability),
        estimatedRecoveryTimeHours: Math.round(recoveryTime * 10) / 10, // Round to 1 decimal place
        resilienceRating: resilienceRating
    };
}
