const BASE_ROAD_COST_UNIT = 150000; // $/unit of compounded damage factor
const BASE_POWER_COST_UNIT = 250000;
const BASE_BUILDING_COST_UNIT = 200000;

// Define how vulnerable each infrastructure type is to each weather parameter (0-1 scale)
const VULNERABILITY_PROFILE = {
    roads: { wind: 0.4, flood: 1.0, ice: 0.2 }, // Roads are highly vulnerable to flood, less to ice
    power: { wind: 0.7, flood: 0.3, ice: 1.0 }, // Power grids highly vulnerable to ice (lines) and wind
    buildings: { wind: 0.6, flood: 0.7, ice: 0.5 } // Buildings vulnerable to wind (roofs) and flood
};

// --- Calculate weather-specific damage factors (0-1, representing intensity beyond a threshold) ---
// These factors quantify the 'severity' of the weather parameter's impact.
let windDamageFactor = 0;
if (windSpeed > 60) { // Threshold for significant wind damage (e.g., tropical storm force winds)
    windDamageFactor = (windSpeed - 60) / 120; // Scales from 0 at 60mph to 1 at 180mph
}
windDamageFactor = Math.min(Math.max(windDamageFactor, 0), 1); // Clamp between 0 and 1

let floodDamageFactor = 0;
if (floodDepth > 0.5) { // Threshold for significant flood damage (e.g., water entering structures/roads)
    floodDamageFactor = (floodDepth - 0.5) / 19.5; // Scales from 0 at 0.5ft to 1 at 20ft
}
floodDamageFactor = Math.min(Math.max(floodDamageFactor, 0), 1); // Clamp between 0 and 1

let iceDamageFactor = 0;
if (iceAccumulation > 0.1) { // Threshold for significant ice damage (e.g., coating power lines, tree limbs)
    iceDamageFactor = (iceAccumulation - 0.1) / 2.9; // Scales from 0 at 0.1in to 1 at 3in
}
iceDamageFactor = Math.min(Math.max(iceDamageFactor, 0), 1); // Clamp between 0 and 1

// --- Adjust base costs based on Region Profile --- 
// Different regions have different baseline costs for infrastructure repair/replacement and asset values.
let regionCostMultiplier = 1;
if (regionProfile === "High Density Urban") {
    regionCostMultiplier = 1.8; // Higher costs due to complexity, higher value assets, higher labor rates
} else if (regionProfile === "Suburban") {
    regionCostMultiplier = 1.2;
} else if (regionProfile === "Rural/Agricultural") {
    regionCostMultiplier = 0.8; // Lower costs due to less dense infrastructure, potentially lower labor
} else if (regionProfile === "Coastal") {
    regionCostMultiplier = 1.5; // Specific vulnerabilities (salinity, storm surge), specialized repairs
}

const adjustedBaseRoadCost = BASE_ROAD_COST_UNIT * regionCostMultiplier;
const adjustedBasePowerCost = BASE_POWER_COST_UNIT * regionCostMultiplier;
const adjustedBaseBuildingCost = BASE_BUILDING_COST_UNIT * regionCostMultiplier;

// --- Calculate combined primary damage for each infrastructure type --- 
// Each infrastructure type's damage is a sum of its vulnerability to each weather factor multiplied by the weather's damage factor and base cost.
let roadsBridgesDamage = 0;
roadsBridgesDamage += adjustedBaseRoadCost * windDamageFactor * VULNERABILITY_PROFILE.roads.wind;
roadsBridgesDamage += adjustedBaseRoadCost * floodDamageFactor * VULNERABILITY_PROFILE.roads.flood;
roadsBridgesDamage += adjustedBaseRoadCost * iceDamageFactor * VULNERABILITY_PROFILE.roads.ice;
roadsBridgesDamage = Math.max(roadsBridgesDamage, 0); // Ensure non-negative damage

let powerGridDamage = 0;
powerGridDamage += adjustedBasePowerCost * windDamageFactor * VULNERABILITY_PROFILE.power.wind;
powerGridDamage += adjustedBasePowerCost * floodDamageFactor * VULNERABILITY_PROFILE.power.flood;
powerGridDamage += adjustedBasePowerCost * iceDamageFactor * VULNERABILITY_PROFILE.power.ice;
powerGridDamage = Math.max(powerGridDamage, 0);

let publicBuildingsDamage = 0;
publicBuildingsDamage += adjustedBaseBuildingCost * windDamageFactor * VULNERABILITY_PROFILE.buildings.wind;
publicBuildingsDamage += adjustedBaseBuildingCost * floodDamageFactor * VULNERABILITY_PROFILE.buildings.flood;
publicBuildingsDamage += adjustedBaseBuildingCost * iceDamageFactor * VULNERABILITY_PROFILE.buildings.ice;
publicBuildingsDamage = Math.max(publicBuildingsDamage, 0);

// --- Apply Infrastructure Resilience Rating Factor --- 
// Higher resilience reduces damage. Rating 1 (Very Low) means minimal reduction, 5 (Very High) means significant reduction.
// Factor ranges from 1.0 (rating 1) to 0.5 (rating 5), implying up to a 50% damage reduction for highly resilient infrastructure.
const resilienceFactor = 1 - ((infrastructureResilienceRating - 1) / 4) * 0.5;
// Ensure resilienceFactor is within a plausible range (e.g., 0.5 to 1.0)
const clampedResilienceFactor = Math.min(Math.max(resilienceFactor, 0.5), 1.0);

roadsBridgesDamage *= clampedResilienceFactor;
powerGridDamage *= clampedResilienceFactor;
publicBuildingsDamage *= clampedResilienceFactor;

// --- Apply Event Duration Factor --- 
// A longer event can lead to more sustained damage, increased erosion, prolonged exposure, and higher total costs.
// Base duration is 24 hours. Events up to 168 hours can add up to 20% to costs.
const durationMultiplier = 1 + Math.min(Math.max((eventDurationHours - 24) / 144, 0), 1) * 0.2; // Max 20% increase for long events

roadsBridgesDamage *= durationMultiplier;
powerGridDamage *= durationMultiplier;
publicBuildingsDamage *= durationMultiplier;

// --- Calculate Total Primary Damage (direct repair/replacement costs) ---
const totalPrimaryDamage = roadsBridgesDamage + powerGridDamage + publicBuildingsDamage;

// --- Calculate Secondary Recovery Costs --- 
// These include indirect costs like economic disruption, emergency response logistics, temporary housing, business losses, etc.
// The cost rate is a percentage of primary damage, scaled by population density impact. 
// It ranges from 10% (low density impact) to 35% (very high density impact) of primary damage.
const secondaryCostRate = 0.10 + ((populationDensityImpactFactor - 1) / 9) * 0.25; // Scales from 0.10 to 0.35
const secondaryRecoveryCosts = totalPrimaryDamage * secondaryCostRate;

// --- Calculate Final Total Estimated Damage Cost ---
const totalEstimatedDamageCost = totalPrimaryDamage + secondaryRecoveryCosts;

return {
    roadsBridgesDamage: roadsBridgesDamage,
    powerGridDamage: powerGridDamage,
    publicBuildingsDamage: publicBuildingsDamage,
    secondaryRecoveryCosts: secondaryRecoveryCosts,
    totalEstimatedDamageCost: totalEstimatedDamageCost
};
