function calculateCosts(inputs) {
  const { weatherEventType, infrastructureType, eventSeverity, affectedAreaSqKm, populationDensityFactor, infrastructureAgeYears, avgMaterialCostPerUnit, avgLaborCostPerHour, estimatedRepairHoursPerUnit, avgDowntimeCostPerHour } = inputs;

  // Validate critical inputs
  if ([eventSeverity, affectedAreaSqKm, avgMaterialCostPerUnit, avgLaborCostPerHour, estimatedRepairHoursPerUnit, avgDowntimeCostPerHour].some(val => val < 0 || isNaN(val))) {
    throw new Error("All numeric inputs must be non-negative.");
  }
  if (eventSeverity < 1 || eventSeverity > 5) {
    throw new Error("Event Severity must be between 1 and 5.");
  }

  // Multipliers based on event type and infrastructure type
  const weatherEventMultipliers = {
    'heavy_snow': { material: 1.0, labor: 1.2, downtime: 1.5 },
    'flood': { material: 2.5, labor: 1.8, downtime: 2.0 },
    'hurricane_typhoon': { material: 3.0, labor: 2.5, downtime: 3.5 },
    'wildfire': { material: 1.8, labor: 1.5, downtime: 1.2 },
    'extreme_wind': { material: 1.5, labor: 1.3, downtime: 1.8 },
    'extreme_heat': { material: 0.8, labor: 1.0, downtime: 1.3 } // More indirect damage/downtime
  };

  const infrastructureMultipliers = {
    'roads_bridges': { material: 1.0, labor: 1.0, complexity_factor: 1.0, base_units_per_sqkm: 5 }, // 5 km of road per sq km area
    'power_grid': { material: 1.8, labor: 1.5, complexity_factor: 1.3, base_units_per_sqkm: 2 }, // 2 power substations/major segments per sq km
    'water_sewer': { material: 1.5, labor: 1.3, complexity_factor: 1.2, base_units_per_sqkm: 3 }, // 3 km of pipe per sq km
    'telecommunications': { material: 1.2, labor: 1.1, complexity_factor: 1.0, base_units_per_sqkm: 4 }, // 4 km of cable per sq km
    'commercial_residential': { material: 0.7, labor: 0.9, complexity_factor: 0.8, base_units_per_sqkm: 10 } // Represents structures rather than linear infrastructure
  };

  const populationDensityMultipliers = {
    'rural': { labor: 0.9, downtime: 0.8, affected_pop_multiplier: 50 }, // 50 people per sq km
    'suburban': { labor: 1.2, downtime: 1.5, affected_pop_multiplier: 500 }, // 500 people per sq km
    'urban': { labor: 1.5, downtime: 2.5, affected_pop_multiplier: 5000 } // 5000 people per sq km
  };

  const eventSevMultiplier = eventSeverity / 2.5; // Scale 1-5 to a reasonable multiplier range around 1.0
  const ageMultiplier = 1 + (infrastructureAgeYears / 50); // Older infrastructure costs more to repair

  // Guard against non-existent multiplier keys (shouldn't happen with select inputs, but good practice)
  const eventM = weatherEventMultipliers[weatherEventType] || { material: 1.0, labor: 1.0, downtime: 1.0 };
  const infraM = infrastructureMultipliers[infrastructureType] || { material: 1.0, labor: 1.0, complexity_factor: 1.0, base_units_per_sqkm: 1 };
  const popM = populationDensityMultipliers[populationDensityFactor] || { labor: 1.0, downtime: 1.0, affected_pop_multiplier: 100 };

  // Estimate total units of infrastructure affected
  // This is a simplification; in reality, depends on specific damage patterns
  const estimatedInfrastructureUnits = affectedAreaSqKm * infraM.base_units_per_sqkm;

  // 1. Calculate Material Damage Cost
  let materialDamageCost = estimatedInfrastructureUnits * avgMaterialCostPerUnit * infraM.material * eventM.material * ageMultiplier * eventSevMultiplier;
  materialDamageCost = Math.max(materialDamageCost, 0); // Ensure non-negative

  // 2. Calculate Labor & Equipment Cost
  let laborAndEquipmentCost = estimatedInfrastructureUnits * estimatedRepairHoursPerUnit * avgLaborCostPerHour * infraM.labor * eventM.labor * popM.labor * eventSevMultiplier;
  laborAndEquipmentCost = Math.max(laborAndEquipmentCost, 0); // Ensure non-negative

  // 3. Calculate Economic Downtime Cost
  // Assuming downtime duration is proportional to repair hours, and affects estimated population
  const totalRepairHours = estimatedInfrastructureUnits * estimatedRepairHoursPerUnit;
  const estimatedAffectedPopulation = affectedAreaSqKm * popM.affected_pop_multiplier;
  let economicDowntimeCost = estimatedAffectedPopulation * totalRepairHours * avgDowntimeCostPerHour * eventM.downtime * popM.downtime * eventSevMultiplier;
  economicDowntimeCost = Math.max(economicDowntimeCost, 0); // Ensure non-negative

  // 4. Calculate Total Repair and Recovery Cost
  const totalRepairAndRecoveryCost = materialDamageCost + laborAndEquipmentCost + economicDowntimeCost;

  return {
    totalRepairAndRecoveryCost: Math.round(totalRepairAndRecoveryCost),
    materialDamageCost: Math.round(materialDamageCost),
    laborAndEquipmentCost: Math.round(laborAndEquipmentCost),
    economicDowntimeCost: Math.round(economicDowntimeCost)
  };
}
