function calculate(inputs) {
  const { 
    locationType, propertyType, propertySizeSqFt, extremeWeatherType, 
    riskMitigationLevel, insuranceCoveragePercent, criticalInfrastructureDependence,
    yearsOfPreparednessHorizon 
  } = inputs;

  // --- Constants and Multipliers ---
  const BASE_INITIAL_COSTS = {
    residential: 5000,
    small_business: 15000
  };
  const BASE_ANNUAL_COSTS = {
    residential: 500,
    small_business: 1500
  };

  const LOCATION_MULTIPLIERS = {
    urban: 1.15,
    suburban: 1.0,
    rural: 0.9
  };

  const WEATHER_INITIAL_MULTIPLIERS = {
    hurricane: 2.2,
    flood: 2.0,
    wildfire: 1.8,
    blizzard: 1.4,
    tornado: 2.1,
    drought: 1.0
  };
  const WEATHER_ANNUAL_MULTIPLIERS = {
    hurricane: 1.6,
    flood: 1.5,
    wildfire: 1.4,
    blizzard: 1.2,
    tornado: 1.6,
    drought: 1.1
  };

  const MITIGATION_LEVEL_INITIAL_MULTIPLIERS = {
    basic: 1.0,
    moderate: 2.5,
    advanced: 5.0
  };
  const MITIGATION_LEVEL_ANNUAL_MULTIPLIERS = {
    basic: 1.0,
    moderate: 1.8,
    advanced: 3.0
  };

  const SIZE_INITIAL_COST_PER_SQFT = {
    basic: 1.5, // e.g., sealing, basic reinforcements
    moderate: 4.0, // e.g., window upgrades, minor structural, generator pad
    advanced: 8.0 // e.g., full structural hardening, safe room, water storage
  };

  const BIZ_INFRA_INITIAL_MULTIPLIERS = {
    low: 1.0,
    medium: 1.3, // e.g., UPS, basic comms redundancy
    high: 1.8 // e.g., generator, satellite comms, hardened data storage
  };
  const BIZ_INFRA_ANNUAL_MULTIPLIERS = {
    low: 1.0,
    medium: 1.2,
    high: 1.5
  };

  const HORIZON_YEARS = {
    short: 3,
    mid: 7,
    long: 15
  };

  const POTENTIAL_SAVINGS_BASE_VALUE_PER_SQFT = {
    residential: 180, // Average property value + contents
    small_business: 300 // Average business asset value + inventory
  };
  const POTENTIAL_SAVINGS_MITIGATION_FACTOR = {
    basic: 0.25, // % of potential damage averted
    moderate: 0.55,
    advanced: 0.85
  };

  // --- Input Validation and Edge Cases ---
  if (propertySizeSqFt <= 0) {
    return {
      initialInvestment: 0,
      annualMaintenanceCost: 0,
      totalEstimatedCost: 0,
      potentialSavings: 0
    };
  }

  const calculatedHorizonYears = HORIZON_YEARS[yearsOfPreparednessHorizon];

  // --- Calculation Steps ---

  // 1. Calculate base initial and annual costs based on property type and location.
  let initialInvestment = BASE_INITIAL_COSTS[propertyType] * LOCATION_MULTIPLIERS[locationType];
  let annualMaintenanceCost = BASE_ANNUAL_COSTS[propertyType] * LOCATION_MULTIPLIERS[locationType];

  // 2. Apply multipliers for extreme weather type and desired mitigation level.
  initialInvestment *= WEATHER_INITIAL_MULTIPLIERS[extremeWeatherType];
  initialInvestment *= MITIGATION_LEVEL_INITIAL_MULTIPLIERS[riskMitigationLevel];
  annualMaintenanceCost *= WEATHER_ANNUAL_MULTIPLIERS[extremeWeatherType];
  annualMaintenanceCost *= MITIGATION_LEVEL_ANNUAL_MULTIPLIERS[riskMitigationLevel];

  // 3. Factor in property size for structural and specific upgrades.
  initialInvestment += propertySizeSqFt * SIZE_INITIAL_COST_PER_SQFT[riskMitigationLevel];

  // 4. Adjust for critical infrastructure dependence for small businesses.
  if (propertyType === 'small_business') {
    initialInvestment *= BIZ_INFRA_INITIAL_MULTIPLIERS[criticalInfrastructureDependence];
    annualMaintenanceCost *= BIZ_INFRA_ANNUAL_MULTIPLIERS[criticalInfrastructureDependence];
  }

  // 5. Apply insurance coverage, ensuring a minimum user investment (e.g., 10% of gross initial cost).
  // Insurance typically covers losses, not proactive preparedness investments, but can offset some larger items.
  // For this calculator, we'll model it as offsetting a portion of the *initial* burden.
  initialInvestment = Math.max(initialInvestment * (1 - insuranceCoveragePercent / 100), initialInvestment * 0.10);

  // 6. Cap annual costs relative to initial investment for realism (e.g., max 20% of initial for maintenance).
  annualMaintenanceCost = Math.min(annualMaintenanceCost, initialInvestment * 0.25);
  // Ensure a minimum annual cost to reflect ongoing needs
  annualMaintenanceCost = Math.max(annualMaintenanceCost, BASE_ANNUAL_COSTS[propertyType] * 0.5); 

  // 7. Calculate total estimated cost over the planning horizon.
  const totalEstimatedCost = initialInvestment + (annualMaintenanceCost * calculatedHorizonYears);

  // 8. Calculate potential cost avoidance/savings from preparedness.
  let potentialSavings = propertySizeSqFt * POTENTIAL_SAVINGS_BASE_VALUE_PER_SQFT[propertyType];
  potentialSavings *= POTENTIAL_SAVINGS_MITIGATION_FACTOR[riskMitigationLevel];
  potentialSavings *= WEATHER_INITIAL_MULTIPLIERS[extremeWeatherType]; // Higher risk weather, higher potential savings

  // Cap potential savings to avoid unrealistic numbers (e.g., max 5x total investment over horizon)
  potentialSavings = Math.min(potentialSavings, totalEstimatedCost * 5);
  // Ensure some minimum savings if any investment is made
  if (initialInvestment > 0 && potentialSavings < initialInvestment * 0.5) {
      potentialSavings = initialInvestment * 0.5;
  }

  return {
    initialInvestment: Math.round(initialInvestment),
    annualMaintenanceCost: Math.round(annualMaintenanceCost),
    totalEstimatedCost: Math.round(totalEstimatedCost),
    potentialSavings: Math.round(potentialSavings)
  };
}
