const initialInvestment = inputs.initialInvestment;
const eventProbability = inputs.eventProbability;
const averageDamageCost = inputs.averageDamageCost;
const damageReductionPercent = inputs.damageReductionPercent;
const projectLifeYears = inputs.projectLifeYears;
const annualMaintenanceCost = inputs.annualMaintenanceCost;

// Input validation to ensure meaningful calculations
if (initialInvestment < 0 || eventProbability < 0 || eventProbability > 1 || averageDamageCost < 0 || damageReductionPercent < 0 || damageReductionPercent > 1 || projectLifeYears <= 0 || annualMaintenanceCost < 0) {
    throw new Error("Please ensure all input values are non-negative, probability and reduction percentages are between 0 and 1, and project life is greater than zero.");
}

// 1. Calculate the effective damage reduction per event
const effectiveDamageReductionPerEvent = averageDamageCost * damageReductionPercent;

// 2. Calculate the expected annual avoided losses (pre-maintenance)
const expectedAnnualAvoidedLosses = effectiveDamageReductionPerEvent * eventProbability;

// 3. Calculate total avoided losses over the project life
const totalAvoidedLosses = expectedAnnualAvoidedLosses * projectLifeYears;

// 4. Calculate total maintenance costs over the project life
const totalMaintenanceCosts = annualMaintenanceCost * projectLifeYears;

// 5. Calculate Net Financial Benefit
const netBenefit = totalAvoidedLosses - initialInvestment - totalMaintenanceCosts;

// 6. Calculate Simple Return on Investment
let simpleROI = 0;
if (initialInvestment > 0) {
    simpleROI = (netBenefit / initialInvestment) * 100;
} else {
    // If initial investment is 0, ROI is considered infinite for positive benefit, 0 otherwise.
    simpleROI = netBenefit > 0 ? Number.MAX_VALUE : 0; // Use MAX_VALUE to represent effectively infinite
}

// 7. Calculate Payback Period
const annualNetSavings = expectedAnnualAvoidedLosses - annualMaintenanceCost;
let paybackPeriodYears = null; // Default to null if not applicable or cannot be calculated

if (initialInvestment === 0) {
    paybackPeriodYears = 0; // Immediate payback if no investment
} else if (annualNetSavings > 0) {
    const calculatedPayback = initialInvestment / annualNetSavings;
    // Cap payback period at project life if it exceeds it, signifying it doesn't pay back within its useful life.
    paybackPeriodYears = Math.min(calculatedPayback, projectLifeYears);
    // If the calculated payback is exactly the project life and the initial investment isn't fully recovered, it's not a full payback.
    // We only set it if it's strictly less than or equal, otherwise null if it goes beyond.
    if (calculatedPayback > projectLifeYears) {
        paybackPeriodYears = null;
    }
} else {
    // If annual net savings are zero or negative, the investment never pays back.
    paybackPeriodYears = null;
}

return {
    totalAvoidedLosses: totalAvoidedLosses,
    netBenefit: netBenefit,
    simpleROI: simpleROI,
    paybackPeriodYears: paybackPeriodYears
};
