/**
 * Calculates potential childcare savings based on new policies.
 * @param {object} inputs - The input values from the form.
 * @param {number} inputs.currentMonthlyChildcareCostPerChild - Current monthly cost per child.
 * @param {number} inputs.numberOfChildren - Number of children requiring care.
 * @param {number} inputs.householdGrossAnnualIncome - Household's gross annual income.
 * @param {'percentage'|'fixed'} inputs.subsidyType - Type of subsidy (percentage or fixed amount).
 * @param {number} inputs.subsidyValue - Value of the subsidy (e.g., 50 for 50%, or 800 for $800).
 * @param {number | null} inputs.policyMonthlyCapPerChild - Optional monthly cap on subsidy per child.
 * @returns {object} Calculated outputs.
 */
function calculate(inputs) {
  const { currentMonthlyChildcareCostPerChild, numberOfChildren, householdGrossAnnualIncome, subsidyType, subsidyValue, policyMonthlyCapPerChild } = inputs;

  // Input validation and default values
  const safeCurrentCostPerChild = Math.max(0, currentMonthlyChildcareCostPerChild || 0);
  const safeNumChildren = Math.max(1, Math.floor(numberOfChildren || 1)); // At least 1 child for calculation relevance
  const safeAnnualIncome = Math.max(0, householdGrossAnnualIncome || 0);
  const safeSubsidyValue = Math.max(0, subsidyValue || 0);
  const safePolicyCapPerChild = (policyMonthlyCapPerChild === null || policyMonthlyCapPerChild === undefined) ? Infinity : Math.max(0, policyMonthlyCapPerChild);

  // 1. Calculate total current monthly childcare cost
  const totalCurrentMonthlyChildcareCost = safeCurrentCostPerChild * safeNumChildren;

  let rawSubsidyPerChild = 0;
  // 2. Determine raw subsidy per child based on type
  if (subsidyType === 'percentage') {
    // SubsidyValue is treated as a percentage (e.g., 50 for 50%)
    rawSubsidyPerChild = (safeSubsidyValue / 100) * safeCurrentCostPerChild;
  } else if (subsidyType === 'fixed') {
    // SubsidyValue is treated as a fixed dollar amount
    rawSubsidyPerChild = safeSubsidyValue;
  } else {
    // Handle unknown subsidy type, effectively no subsidy
    rawSubsidyPerChild = 0;
  }

  // 3. Apply the policy monthly cap per child
  const actualSubsidyPerChild = Math.min(rawSubsidyPerChild, safePolicyCapPerChild);

  // 4. Calculate total monthly subsidy
  const totalMonthlySubsidy = actualSubsidyPerChild * safeNumChildren;

  // 5. Calculate new total monthly childcare cost
  const newTotalMonthlyChildcareCost = Math.max(0, totalCurrentMonthlyChildcareCost - totalMonthlySubsidy);

  // 6. Calculate monthly and annual savings
  const monthlySavings = totalMonthlySubsidy; // Savings directly correspond to the subsidy received
  const annualSavings = monthlySavings * 12;

  // 7. Calculate disposable income impact
  const annualDisposableIncomeIncrease = annualSavings;
  const disposableIncomeIncreasePercentage = safeAnnualIncome > 0 
    ? (annualDisposableIncomeIncrease / safeAnnualIncome) * 100 
    : 0; // Avoid division by zero

  return {
    totalCurrentMonthlyChildcareCost: totalCurrentMonthlyChildcareCost,
    newTotalMonthlyChildcareCost: newTotalMonthlyChildcareCost,
    monthlySavings: monthlySavings,
    annualSavings: annualSavings,
    annualDisposableIncomeIncrease: annualDisposableIncomeIncrease,
    disposableIncomeIncreasePercentage: disposableIncomeIncreasePercentage
  };
}
