function calculate(inputs) {
  const monthlyEssentialExpenses = parseFloat(inputs.monthlyEssentialExpenses);
  const monthlyNonEssentialExpenses = parseFloat(inputs.monthlyNonEssentialExpenses);
  const currentEmergencyFund = parseFloat(inputs.currentEmergencyFund);
  const targetCoverageMonths = parseInt(inputs.targetCoverageMonths);
  const householdNetIncome = parseFloat(inputs.householdNetIncome);
  const jobLossScenario = inputs.jobLossScenario;
  const incomeOfLostEarner = parseFloat(inputs.incomeOfLostEarner || 0);
  const estimatedUnemploymentBenefit = parseFloat(inputs.estimatedUnemploymentBenefit || 0);
  const unemploymentBenefitDurationMonths = parseInt(inputs.unemploymentBenefitDurationMonths || 0);

  // Input validation for non-required fields in job loss scenario
  if (jobLossScenario !== 'none') {
    if (isNaN(incomeOfLostEarner) || incomeOfLostEarner < 0) return { error: 'Please enter a valid income for the lost earner.' };
    if (isNaN(estimatedUnemploymentBenefit) || estimatedUnemploymentBenefit < 0) return { error: 'Please enter a valid unemployment benefit amount.' };
    if (isNaN(unemploymentBenefitDurationMonths) || unemploymentBenefitDurationMonths < 0) return { error: 'Please enter a valid unemployment benefit duration.' };
  }

  // 1. Calculate required fund for a 'normal' scenario (no job loss, covering all expenses)
  const totalCurrentMonthlyExpenses = monthlyEssentialExpenses + monthlyNonEssentialExpenses;
  const requiredFundForNormalScenario = totalCurrentMonthlyExpenses * targetCoverageMonths;

  // 2. Calculate required fund for a 'job loss' scenario (cutting non-essentials)
  let jobLossRequiredFund = 0;
  if (jobLossScenario !== 'none') {
    const crisisMonthlyExpenses = monthlyEssentialExpenses; // Assume non-essentials are cut
    const remainingIncome = householdNetIncome - incomeOfLostEarner;

    const monthsWithBenefits = Math.min(targetCoverageMonths, unemploymentBenefitDurationMonths);
    const monthsWithoutBenefits = targetCoverageMonths - monthsWithBenefits;

    // Calculate monthly deficit/surplus during crisis periods
    const monthlyNetCashFlowWithBenefits = remainingIncome + estimatedUnemploymentBenefit - crisisMonthlyExpenses;
    const monthlyNetCashFlowWithoutBenefits = remainingIncome - crisisMonthlyExpenses;

    // The fund needs to cover the negative cash flow
    let deficitWithBenefits = 0;
    if (monthlyNetCashFlowWithBenefits < 0) {
      deficitWithBenefits = Math.abs(monthlyNetCashFlowWithBenefits);
    }

    let deficitWithoutBenefits = 0;
    if (monthlyNetCashFlowWithoutBenefits < 0) {
      deficitWithoutBenefits = Math.abs(monthlyNetCashFlowWithoutBenefits);
    }

    jobLossRequiredFund = (deficitWithBenefits * monthsWithBenefits) + (deficitWithoutBenefits * monthsWithoutBenefits);
    jobLossRequiredFund = Math.max(0, jobLossRequiredFund); // Ensure it's not negative if income covers expenses
  }

  // 3. Determine the final required emergency fund (the most conservative of the two scenarios)
  const finalRequiredFund = Math.max(requiredFundForNormalScenario, jobLossRequiredFund);

  // 4. Calculate current fund coverage in months (based on essential expenses only, for a true crisis measure)
  const currentFundCoverageMonths = monthlyEssentialExpenses > 0 ? (currentEmergencyFund / monthlyEssentialExpenses) : 0;

  // 5. Calculate fund deficit/surplus
  const fundDeficitSurplus = currentEmergencyFund - finalRequiredFund;

  // 6. Determine fund adequacy message
  let fundAdequacyMessage;
  if (fundDeficitSurplus >= 0) {
    fundAdequacyMessage = "Adequate! Your fund meets or exceeds the target.";
  } else if (fundDeficitSurplus > - (finalRequiredFund * 0.25)) { // Within 25% of target
    fundAdequacyMessage = "Slight Shortfall. You are close to your target, but more savings are needed.";
  } else if (fundDeficitSurplus > - (finalRequiredFund * 0.75)) { // Within 75% of target
    fundAdequacyMessage = "Significant Shortfall. Your fund needs substantial growth to reach the target.";
  } else {
    fundAdequacyMessage = "Critical Shortfall. Your emergency fund requires urgent attention.";
  }

  // 7. Calculate recommended monthly savings and time to reach target
  let recommendedMonthlySavings = 0;
  let timeToReachTarget = 0;

  if (fundDeficitSurplus < 0) {
    const deficit = Math.abs(fundDeficitSurplus);
    // Assume they can save their discretionary income (total income - essential - non-essential)
    // Or, if already cutting non-essentials, then income - essential
    const availableSavings = householdNetIncome - (monthlyEssentialExpenses + monthlyNonEssentialExpenses);

    if (availableSavings > 0) {
      recommendedMonthlySavings = availableSavings;
      timeToReachTarget = Math.ceil(deficit / recommendedMonthlySavings);
    } else if (householdNetIncome - monthlyEssentialExpenses > 0) {
        // If they can't save from full expenses, assume they cut non-essentials to save
        recommendedMonthlySavings = householdNetIncome - monthlyEssentialExpenses;
        timeToReachTarget = Math.ceil(deficit / recommendedMonthlySavings);
    } else {
      // Cannot save anything even by cutting non-essentials, or income is too low
      recommendedMonthlySavings = 0; // Or a symbolic minimum
      timeToReachTarget = Infinity;
    }
  }

  return {
    requiredEmergencyFund: finalRequiredFund,
    currentFundCoverageMonths: currentFundCoverageMonths,
    fundAdequacyMessage: fundAdequacyMessage,
    fundDeficitSurplus: fundDeficitSurplus,
    recommendedMonthlySavings: recommendedMonthlySavings,
    timeToReachTarget: timeToReachTarget === Infinity ? 'N/A' : timeToReachTarget
  };
}
