const recentGameCount = inputs.recentGameCount;
const recentAvgPoints = inputs.recentAvgPoints;
const historicalAvgPoints = inputs.historicalAvgPoints;
const injuryImpactFactor = inputs.injuryImpactFactor;
const opponentDefensiveRank = inputs.opponentDefensiveRank;
const expectedRoleMultiplier = inputs.expectedRoleMultiplier;
const ageFactor = inputs.ageFactor;
const adpValue = inputs.adpValue;

// Input validation and default values for robustness
const validatedRecentGameCount = Math.max(0, recentGameCount || 0);
const validatedRecentAvgPoints = recentAvgPoints || 0;
const validatedHistoricalAvgPoints = historicalAvgPoints || 0;
const validatedInjuryImpactFactor = Math.max(0, Math.min(1, injuryImpactFactor || 0));
const validatedOpponentDefensiveRank = Math.max(1, Math.min(32, opponentDefensiveRank || 16)); // Default to average rank
const validatedExpectedRoleMultiplier = Math.max(0.5, Math.min(1.5, expectedRoleMultiplier || 1)); // Default to 1 (no change)
const validatedAgeFactor = Math.max(0.8, Math.min(1.2, ageFactor || 1)); // Default to 1 (prime)
const validatedAdpValue = Math.max(1, Math.min(200, adpValue || 150)); // Default to a later pick if not provided

let basePoints;
// Weight recent performance more heavily if there's sufficient recent data
const recentWeight = validatedRecentGameCount > 0 ? Math.min(1, validatedRecentGameCount / 8) : 0; // Up to 8 games for full recent weight
const historicalWeight = 1 - recentWeight;

if (validatedRecentGameCount === 0 && validatedHistoricalAvgPoints === 0) {
    basePoints = 5; // Default low base points if no data at all
} else if (validatedRecentGameCount === 0) {
    basePoints = validatedHistoricalAvgPoints; // Only historical data available
} else if (validatedHistoricalAvgPoints === 0) {
    basePoints = validatedRecentAvgPoints; // Only recent data available
} else {
    basePoints = (validatedRecentAvgPoints * recentWeight) + (validatedHistoricalAvgPoints * historicalWeight);
}

// Ensure basePoints is never negative or excessively low baseline
basePoints = Math.max(0.1, basePoints);

// Adjust for injury status: A higher factor means a greater point reduction.
let adjustedPoints = basePoints * (1 - validatedInjuryImpactFactor);

// Adjust for opponent strength: Lower rank (e.g., 1) means tougher defense, higher rank (e.g., 32) means easier.
// The formula applies a potential swing of +/- 15% for extreme matchups.
// Average rank is 16. (16 - rank) will be positive for tough opps, negative for easy.
const opponentImpact = 1 + ((16 - validatedOpponentDefensiveRank) / 32) * 0.15; 
adjustedPoints *= opponentImpact;

// Adjust for expected role/volume: Directly scales projected points.
adjustedPoints *= validatedExpectedRoleMultiplier;

// Adjust for age-related performance: Accounts for rising stars or declining veterans.
adjustedPoints *= validatedAgeFactor;

// Final projected points, capped at a realistic maximum for fantasy scoring (e.g., 50 points per game)
const projectedPoints = Math.max(0, Math.min(50, adjustedPoints));

// Calculate Market Value Score
// This score compares the player's projected performance against their market perception (ADP).
// Higher projected points and lower ADP (meaning they are drafted earlier, but perhaps still a bargain) result in a higher value score.

// Normalize projected points to a scale where typical elite performance (e.g., 25 points) is a benchmark.
const pointsNormalized = projectedPoints / 25; 

// Normalize ADP: Lower ADP (closer to 1) should yield a higher value component.
// ADP of 1 yields (200-1)/100 = 1.99; ADP of 200 yields (200-200)/100 = 0.
const adpNormalized = (200 - validatedAdpValue) / 100; 

// Market value is a weighted average of normalized points and normalized ADP.
// Projected points are given more weight as they represent on-field performance.
const marketValueScore = (pointsNormalized * 0.7 + adpNormalized * 0.3) * 100;
// Cap market value score between 0 and 200 for clarity.
const finalMarketValueScore = Math.round(Math.max(0, Math.min(200, marketValueScore)));

let draftRecommendation;
if (finalMarketValueScore >= 140) {
    draftRecommendation = "Elite Value Pick";
} else if (finalMarketValueScore >= 110) {
    draftRecommendation = "Strong Value Pick";
} else if (finalMarketValueScore >= 80) {
    draftRecommendation = "Fair Value Pick";
} else if (finalMarketValueScore >= 50) {
    draftRecommendation = "Situational Pick";
} else {
    draftRecommendation = "Risky Pick / Avoid";
}

return {
    projectedPoints: parseFloat(projectedPoints.toFixed(2)),
    marketValueScore: finalMarketValueScore,
    draftRecommendation: draftRecommendation
};
