const { area, area_unit, target_nutrient_amount, target_nutrient_amount_unit, fertilizer_n_percent, fertilizer_p_percent, fertilizer_k_percent, nutrient_to_calculate } = inputs;

// Input validation and normalization
if (area <= 0 || target_nutrient_amount <= 0) {
    throw new Error("Area and desired nutrient amount must be positive values.");
}

if (fertilizer_n_percent < 0 || fertilizer_n_percent > 100 ||
    fertilizer_p_percent < 0 || fertilizer_p_percent > 100 ||
    fertilizer_k_percent < 0 || fertilizer_k_percent > 100) {
    throw new Error("Fertilizer nutrient percentages must be between 0 and 100.");
}

let actual_nutrient_percent;

switch (nutrient_to_calculate.toUpperCase()) {
    case 'N':
        actual_nutrient_percent = fertilizer_n_percent;
        break;
    case 'P':
        actual_nutrient_percent = fertilizer_p_percent;
        break;
    case 'K':
        actual_nutrient_percent = fertilizer_k_percent;
        break;
    default:
        throw new Error("Invalid nutrient to calculate. Choose N, P, or K.");
}

if (actual_nutrient_percent === 0) {
    throw new Error(`The selected fertilizer has 0% ${nutrient_to_calculate}. Calculation based on this nutrient is impossible.`);
}

// Determine unit factors and output units dynamically based on target_nutrient_amount_unit
let target_area_divisor;
let output_weight_unit;
let output_spread_rate_unit;
let area_for_calc; // Area normalized to match the 'per unit' of target_nutrient_amount_unit

const area_lower = area_unit.toLowerCase();
const target_unit_lower = target_nutrient_amount_unit.toLowerCase();

// Normalize area to a base unit for internal consistency before applying divisor
let base_area_sq_ft = (area_lower === 'sq_m') ? area * 10.7639 : area; // Convert all to sq_ft
let base_area_sq_m = (area_lower === 'sq_ft') ? area * 0.092903 : area; // Convert all to sq_m

if (target_unit_lower === 'lbs_per_1000_sq_ft') {
    target_area_divisor = 1000;
    output_weight_unit = 'lbs';
    output_spread_rate_unit = 'lbs per 1000 sq ft';
    area_for_calc = base_area_sq_ft; // Use sq_ft for calculations consistent with target unit
} else if (target_unit_lower === 'kg_per_100_sq_m') {
    target_area_divisor = 100;
    output_weight_unit = 'kg';
    output_spread_rate_unit = 'kg per 100 sq m';
    area_for_calc = base_area_sq_m; // Use sq_m for calculations consistent with target unit
} else {
    throw new Error("Invalid target nutrient amount unit. Please use 'lbs_per_1000_sq_ft' or 'kg_per_100_sq_m'.");
}

// Convert percentage to decimal
const actual_nutrient_ratio = actual_nutrient_percent / 100;

// Calculate total pure nutrient needed based on the chosen unit system
const total_pure_nutrient_needed = (area_for_calc / target_area_divisor) * target_nutrient_amount;

// Calculate total fertilizer weight needed
const total_fertilizer_weight = total_pure_nutrient_needed / actual_nutrient_ratio;

// Calculate recommended spread rate (amount of *fertilizer* per standard area unit)
// This is essentially the target_nutrient_amount scaled by the fertilizer's concentration
const recommended_spread_rate = target_nutrient_amount / actual_nutrient_ratio;

return {
    total_fertilizer_weight: total_fertilizer_weight,
    total_fertilizer_weight_unit: output_weight_unit,
    recommended_spread_rate: recommended_spread_rate,
    recommended_spread_rate_unit: output_spread_rate_unit
};
