const annual_return_decimal = inputs.annual_investment_return / 100;
const inflation_rate_decimal = inputs.inflation_rate / 100;
const swr_decimal = inputs.safe_withdrawal_rate / 100;

let capital_needed_output = 0;
let years_to_fire_output = 0;
let monthly_retirement_income_output = 0;

if (swr_decimal <= 0.001) { // Prevent division by zero or near-zero SWR
    capital_needed_output = 9999999999; // Effectively infinite
    years_to_fire_output = 999; // Effectively infinite
    monthly_retirement_income_output = 0;
} else if (inputs.desired_annual_spending === 0) {
    capital_needed_output = 0;
    years_to_fire_output = 0;
    monthly_retirement_income_output = 0;
} else {
    const fire_capital_current_dollars = inputs.desired_annual_spending / swr_decimal;

    let current_portfolio_value = inputs.current_savings;
    let months_elapsed = 0;
    let target_capital_inflated = fire_capital_current_dollars; // This target will inflate over time

    const monthly_investment_growth_rate = Math.pow(1 + annual_return_decimal, 1/12) - 1;
    const max_months_for_calculation = 12 * 150; // Cap at 150 years to prevent infinite loops

    while (current_portfolio_value < target_capital_inflated && months_elapsed < max_months_for_calculation) {
        current_portfolio_value *= (1 + monthly_investment_growth_rate);
        current_portfolio_value += inputs.monthly_savings;
        months_elapsed++;

        if (months_elapsed % 12 === 0) { // Adjust target capital for inflation annually
            target_capital_inflated *= (1 + inflation_rate_decimal);
        }
    }

    if (months_elapsed >= max_months_for_calculation) {
        capital_needed_output = 9999999999; // Indicate not achievable within a reasonable timeframe
        years_to_fire_output = 999;
        monthly_retirement_income_output = 0;
    } else {
        capital_needed_output = target_capital_inflated;
        years_to_fire_output = months_elapsed / 12;
        // The monthly retirement income should be based on the inflated capital needed
        // to maintain the *original* desired purchasing power.
        monthly_retirement_income_output = (capital_needed_output * swr_decimal) / 12;
    }
}

return {
    capital_needed: capital_needed_output,
    years_to_fire: years_to_fire_output,
    monthly_retirement_income: monthly_retirement_income_output,
};
