function calculate({ floor_area, occupancy_category, num_exits, avg_exit_width, is_sprinklered, structural_material, max_travel_distance }) {
    // Occupant Load Factor (sq ft per person) based on IBC/NFPA common values
    let olf;
    switch (occupancy_category.toLowerCase()) {
        case 'assembly': olf = 15; break; // Unfixed seats
        case 'business': olf = 100; break;
        case 'educational': olf = 20; break;
        case 'industrial': olf = 200; break;
        case 'residential': olf = 200; break;
        case 'mercantile': olf = 50; break; // General average
        default: olf = 100; // Default for other or unknown, usually Business
    }

    // 1. Safe Occupant Capacity
    const safeOccupantCapacity = Math.floor(floor_area / olf);
    const finalSafeOccupantCapacity = Math.max(1, safeOccupantCapacity); // Minimum 1 occupant

    // 2. Required Total Egress Width (in inches)
    // Standard code requires 0.2 inches per person of egress width. (IBC)
    // Also, minimum 36 inches per exit for a standard door (IBC).
    const minWidthPerExit = 32; // IBC minimum clear width for single door is 32 inches, let's use 32 for total width per exit 
    const codeRequiredWidthPerPerson = finalSafeOccupantCapacity * 0.2;
    // Total required egress width is the greater of capacity-based or sum of minimum exit widths
    const totalRequiredEgressWidth = Math.max(codeRequiredWidthPerPerson, num_exits * minWidthPerExit);

    // 3. Required Fire Resistance Rating (in hours)
    let baseFireResistance;
    switch (structural_material.toLowerCase()) {
        case 'concrete': baseFireResistance = 4; break; // Often Type I or II-A construction
        case 'steel': baseFireResistance = 2; break; // Assumes protected steel (e.g., Type II-A)
        case 'wood frame': baseFireResistance = 0.75; break; // Typical unprotected/minimal (e.g., Type V-B)
        case 'heavy timber': baseFireResistance = 1.5; break; // (e.g., Type IV)
        default: baseFireResistance = 1; // General construction default, often minimum for some elements
    }

    // Apply sprinkler system reduction
    // Sprinklers can often reduce required fire resistance ratings by 1 hour (IBC, NFPA), but not below a certain minimum.
    let requiredFireResistance = baseFireResistance;
    if (is_sprinklered === 1) {
        requiredFireResistance = Math.max(0.5, baseFireResistance - 1); // Reduce by 1 hour, but not below 0.5 hours (common absolute minimum for some elements)
    }
    // Ensure a practical minimum, e.g., 1 hour for major structural elements even with sprinklers is common
    requiredFireResistance = Math.max(1, requiredFireResistance);
    // Round to nearest 0.5 hour for typical code ratings (0.5, 1, 1.5, 2, 3, 4 hours)
    requiredFireResistance = Math.round(requiredFireResistance * 2) / 2;

    // Optional check for travel distance, not an output for this specific calculator definition
    let maxAllowedTravelDistanceBase;
    switch (occupancy_category.toLowerCase()) {
        case 'assembly': maxAllowedTravelDistanceBase = 200; break;
        case 'business': maxAllowedTravelDistanceBase = 200; break;
        case 'educational': maxAllowedTravelDistanceBase = 200; break;
        case 'residential': maxAllowedTravelDistanceBase = 200; break;
        default: maxAllowedTravelDistanceBase = 200; // General case for other occupancies
    }
    // Sprinkler systems often allow increased travel distance, e.g., by 50 feet or 25% for some occupancies.
    const maxAllowedTravelDistance = is_sprinklered === 1 ? maxAllowedTravelDistanceBase + 50 : maxAllowedTravelDistanceBase;
    const travelDistanceCompliance = max_travel_distance <= maxAllowedTravelDistance; // true/false

    return {
        required_fire_resistance: requiredFireResistance,
        safe_occupant_capacity: finalSafeOccupantCapacity,
        required_total_egress_width: totalRequiredEgressWidth
    };
}
