const age = parseFloat(inputs.currentAge) || 30;
const savings = parseFloat(inputs.currentSavings) || 0;
const income = parseFloat(inputs.annualIncome) || 0;
const expenses = parseFloat(inputs.annualExpenses) || 0;
const r = (parseFloat(inputs.annualReturn) || 0) / 100;
const swr = (parseFloat(inputs.withdrawalRate) || 0) / 100;

if (swr === 0) return { fiNumber: 0, yearsToFi: 0, fiAge: age };

const fiNumber = expenses / swr;
const annualSavings = income - expenses;

if (savings >= fiNumber) {
    return { fiNumber: fiNumber, yearsToFi: 0, fiAge: age };
}

if (annualSavings <= 0) {
    // Never reach FI if you don't save
    return { fiNumber: fiNumber, yearsToFi: 999, fiAge: 999 };
}

// Iterate to find years
// FV = PV*(1+r)^t + PMT*[((1+r)^t - 1)/r]
// This requires solving for t, which is complex algebra.
// Simpler loop approach:

let currentPot = savings;
let years = 0;
while (currentPot < fiNumber && years < 100) {
    currentPot = (currentPot * (1 + r)) + annualSavings;
    years++;
}

return {
    fiNumber: fiNumber,
    yearsToFi: years,
    fiAge: age + years
};
