function calculate() {
    const currentAge = inputs.currentAge;
    const currentSavings = inputs.currentSavings;
    const monthlySavings = inputs.monthlySavings;
    const annualReturnRate = inputs.annualReturnRate / 100; // Nominal
    const inflationRate = inputs.inflationRate / 100;
    const desiredAnnualExpenses = inputs.desiredAnnualExpenses;
    const safeWithdrawalRate = inputs.safeWithdrawalRate / 100;

    const maxYears = 150; // Cap to prevent infinite loops, simulating a very long human lifespan

    // Handle invalid input combinations
    if (desiredAnnualExpenses <= 0 || safeWithdrawalRate <= 0) {
        return {
            fiNumber: 0,
            yearsToFI: 0,
            projectedRetirementAge: currentAge
        };
    }

    // Calculate real annual return rate (inflation-adjusted)
    const realAnnualReturnRate = (1 + annualReturnRate) / (1 + inflationRate) - 1;

    // Calculate target FI number in today's dollars using the safe withdrawal rate
    const targetFiTodayDollars = desiredAnnualExpenses / safeWithdrawalRate;

    let yearsToFI = 0;
    let accumulatedSavingsReal = currentSavings; // Track savings in today's dollars

    // If already FI, return 0 years
    if (currentSavings >= targetFiTodayDollars) {
        return {
            fiNumber: currentSavings, // In today's dollars, as it's already reached
            yearsToFI: 0,
            projectedRetirementAge: currentAge
        };
    }

    // Iterate year by year to find years to FI
    while (accumulatedSavingsReal < targetFiTodayDollars && yearsToFI < maxYears) {
        yearsToFI++;

        // Grow current savings with the real (inflation-adjusted) return rate
        accumulatedSavingsReal *= (1 + realAnnualReturnRate);

        // Add annual contributions (monthly savings * 12) - assumed to be in today's dollars
        accumulatedSavingsReal += monthlySavings * 12;

        // Conditional check to break if target is clearly unreachable (e.g., negative real growth and insufficient savings)
        if (realAnnualReturnRate <= 0 && monthlySavings * 12 <= 0 && accumulatedSavingsReal < targetFiTodayDollars && yearsToFI > 100) {
            yearsToFI = Infinity; // Indicate unreachability
            break;
        }
    }

    let projectedRetirementAge = currentAge + yearsToFI;

    let fiNumberFutureDollars;
    if (yearsToFI === Infinity) {
        // If FI is never reached, use the maximum projected time to inflate the target
        fiNumberFutureDollars = targetFiTodayDollars * Math.pow((1 + inflationRate), maxYears);
        projectedRetirementAge = Infinity; // Set age to Infinity if never reached
    } else {
        // Inflate the target FI number from today's dollars to future dollars at the time of FI
        fiNumberFutureDollars = targetFiTodayDollars * Math.pow((1 + inflationRate), yearsToFI);
    }

    return {
        fiNumber: Math.max(0, fiNumberFutureDollars), // Ensure non-negative
        yearsToFI: yearsToFI === Infinity ? Infinity : Math.max(0, yearsToFI), // Ensure non-negative or Infinity
        projectedRetirementAge: projectedRetirementAge === Infinity ? Infinity : Math.max(currentAge, projectedRetirementAge) // Ensure at least current age or Infinity
    };
}
