function calculate({ kFactorValue, pressureValue, flowValue }) {
    let kFactor = parseFloat(kFactorValue);
    let pressure = parseFloat(pressureValue);
    let flow = parseFloat(flowValue);

    const inputsProvided = [kFactor, pressure, flow].filter(val => !isNaN(val) && val !== null).length;

    if (inputsProvided < 2) {
        return {
            calculatedKFactor: null,
            calculatedPressure: null,
            calculatedFlow: null,
            calculationInfo: "Please provide at least two values (K-Factor, Pressure, or Flow) to perform a calculation."
        };
    }

    let resultKFactor = null;
    let resultPressure = null;
    let resultFlow = null;
    let info = "";

    // Prioritize calculation based on which two values are provided
    if (!isNaN(kFactor) && !isNaN(pressure)) {
        // Calculate Flow (Q) using Q = K * sqrt(P)
        resultFlow = kFactor * Math.sqrt(pressure > 0 ? pressure : 0);
        info = `Calculated Flow Rate using K-Factor (${kFactor}) and Pressure (${pressure} PSI).`;
        resultKFactor = kFactor; // Reflect input for context
        resultPressure = pressure; // Reflect input for context

        if (!isNaN(flow) && Math.abs(resultFlow - flow) / flow > 0.05 && flow > 0) { // Check if provided flow deviates significantly
            info += `\nNote: The provided Flow Rate (${flow} GPM) deviates significantly from the calculated flow (${resultFlow.toFixed(2)} GPM) based on the given K-Factor and Pressure.`;
        }

    } else if (!isNaN(flow) && !isNaN(pressure)) {
        // Calculate K-Factor using K = Q / sqrt(P)
        if (pressure <= 0) {
            return { calculatedKFactor: null, calculatedPressure: null, calculatedFlow: null, calculationInfo: "Pressure must be greater than zero to calculate K-Factor." };
        }
        resultKFactor = flow / Math.sqrt(pressure);
        info = `Calculated K-Factor using Flow Rate (${flow} GPM) and Pressure (${pressure} PSI).`;
        resultFlow = flow;
        resultPressure = pressure;

        if (!isNaN(kFactor) && Math.abs(resultKFactor - kFactor) / kFactor > 0.05 && kFactor > 0) {
            info += `\nNote: The provided K-Factor (${kFactor}) deviates significantly from the calculated K-Factor (${resultKFactor.toFixed(2)}) based on the given Flow and Pressure.`;
        }

    } else if (!isNaN(flow) && !isNaN(kFactor)) {
        // Calculate Pressure (P) using P = (Q / K)^2
        if (kFactor === 0) {
            return { calculatedKFactor: null, calculatedPressure: null, calculatedFlow: null, calculationInfo: "K-Factor cannot be zero when calculating pressure." };
        }
        resultPressure = Math.pow(flow / kFactor, 2);
        info = `Calculated Pressure using Flow Rate (${flow} GPM) and K-Factor (${kFactor}).`;
        resultFlow = flow;
        resultKFactor = kFactor;

        if (!isNaN(pressure) && Math.abs(resultPressure - pressure) / pressure > 0.05 && pressure > 0) {
            info += `\nNote: The provided Pressure (${pressure} PSI) deviates significantly from the calculated pressure (${resultPressure.toFixed(2)} PSI) based on the given Flow and K-Factor.`;
        }

    } else {
        // This case should not be reached if inputsProvided < 2 check is correct
        return {
            calculatedKFactor: null,
            calculatedPressure: null,
            calculatedFlow: null,
            calculationInfo: "Invalid input combination. Please provide two values (K-Factor, Pressure, or Flow)."
        };
    }

    // Apply conditional logic and caps for practical feedback
    if (resultPressure !== null) {
        if (resultPressure < 7) { // Minimum operating pressure for many standard heads
            info += "\nWarning: Calculated pressure is very low (< 7 PSI). This might be insufficient for effective spray patterns and could indicate an underperforming system or improper design assumptions per NFPA 13.";
        } else if (resultPressure > 175) { // Max pressure for standard sprinklers (usually 175 PSI or 300 PSI)
            info += "\nCaution: Calculated pressure is very high (> 175 PSI). Verify the working pressure rating of the sprinkler head and system components.";
        }
    }

    if (resultFlow !== null) {
        // Example condition based on hazard and head type (simplified for a single head)
        // For a typical K=5.6 head in a light hazard, 20 GPM is often a minimum. This is a simplified check.
        if (resultKFactor === 5.6 && resultFlow < 20 && resultPressure > 5) {
             info += "\nNote: For a common K=5.6 sprinkler, a flow rate below 20 GPM at adequate pressure may be less than ideal for some light hazard area design requirements. Consult NFPA 13.";
        }
        if (resultFlow > 500) { // Very high flow for a single sprinkler head
            info += "\nCaution: Calculated flow rate is extremely high for a single sprinkler head. Double-check your input values and design parameters.";
        }
    }

    if (resultKFactor !== null) {
        if (resultKFactor < 2.8) {
            info += "\nNote: Very small K-factors (< 2.8) are rare for standard sprinklers and typically indicate micro-sprinklers or specialty nozzles.";
        } else if (resultKFactor > 25) {
            info += "\nNote: Very large K-factors (> 25) are typical for large orifice, extended coverage, or deluge nozzles, not standard sprinklers. Ensure this is appropriate for your application.";
        }
    }

    return {
        calculatedKFactor: resultKFactor,
        calculatedPressure: resultPressure,
        calculatedFlow: resultFlow,
        calculationInfo: info
    };
}
