const GALLONS_PER_CUBIC_FOOT = 7.48052;
const MINUTES_PER_HOUR = 60;
const INCHES_PER_FOOT = 12;
const LOOP_LENGTH_BUFFER_FACTOR = 1.15; // Accounts for turns, lead-ins, manifolds

// 1. Calculate Total Heat Load
const totalHeatLoad = inputs.heatedArea * inputs.heatLoadPerSqFt; // BTU/hr

// 2. Determine Max Single Loop Length based on Pipe Diameter (example rule of thumb)
let maxSingleLoopLength;
const pipeDiameterInches = inputs.pipeOD; 
if (pipeDiameterInches < 0.5) { // e.g., 3/8" PEX (OD usually ~0.5")
    maxSingleLoopLength = 250; // ft
} else if (pipeDiameterInches >= 0.5 && pipeDiameterInches < 0.7) { // e.g., 1/2" PEX (OD usually ~0.625")
    maxSingleLoopLength = 300; // ft
} else if (pipeDiameterInches >= 0.7 && pipeDiameterInches < 0.85) { // e.g., 5/8" PEX (OD usually ~0.75")
    maxSingleLoopLength = 400; // ft
} else { // e.g., 3/4" PEX (OD usually ~0.875") or larger
    maxSingleLoopLength = 500; // ft
}

// 3. Calculate Total Pipe Length Required for the Area
// The density of pipe required per area, then scaled for turns/leads
const pipeFootagePerSqFt = INCHES_PER_FOOT / inputs.loopSpacing; // ft of pipe per sq ft of area
const totalRequiredPipeLength = inputs.heatedArea * pipeFootagePerSqFt * LOOP_LENGTH_BUFFER_FACTOR; // ft

// 4. Calculate Number of Loops
const numLoops = Math.ceil(totalRequiredPipeLength / maxSingleLoopLength);

// 5. Calculate Heat Load Per Loop
const heatLoadPerLoop = totalHeatLoad / numLoops; // BTU/hr per loop

// 6. Calculate Flow Rate Per Loop (GPM)
// Q = m_dot * Cp * dT  =>  m_dot_lb_per_hr = Q / (Cp * dT)
const massFlowRate_lb_per_hr = heatLoadPerLoop / (inputs.fluidSpecificHeat * inputs.tempDrop);

// Convert mass flow rate (lb/hr) to volume flow rate (GPM)
// volume_ft3_per_hr = mass_lb_per_hr / density_lb_per_ft3
const volumeFlowRate_ft3_per_hr = massFlowRate_lb_per_hr / inputs.fluidDensity;
// volume_gal_per_hr = volume_ft3_per_hr * GALLONS_PER_CUBIC_FOOT
const volumeFlowRate_gal_per_hr = volumeFlowRate_ft3_per_hr * GALLONS_PER_CUBIC_FOOT;
// volume_GPM = volume_gal_per_hr / MINUTES_PER_HOUR
const flowRatePerLoop = volumeFlowRate_gal_per_hr / MINUTES_PER_HOUR; // GPM

// 7. Calculate Total System Flow Rate (GPM)
const totalFlowRate = flowRatePerLoop * numLoops;

return {
    totalPipeLength: totalRequiredPipeLength,
    numLoops: numLoops,
    flowRatePerLoop: flowRatePerLoop,
    totalFlowRate: totalFlowRate
};
