function calculate({ footing_length_m, footing_width_m, applied_load_kN, allowable_bearing_capacity_kPa, soil_elastic_modulus_kPa, soil_poissons_ratio }) {
  // Input validation and sanitization
  footing_length_m = Math.max(0.1, parseFloat(footing_length_m));
  footing_width_m = Math.max(0.1, parseFloat(footing_width_m));
  applied_load_kN = Math.max(0.1, parseFloat(applied_load_kN));
  allowable_bearing_capacity_kPa = Math.max(0.1, parseFloat(allowable_bearing_capacity_kPa));
  soil_elastic_modulus_kPa = Math.max(100, parseFloat(soil_elastic_modulus_kPa)); // Min Es to prevent division by zero or unrealistic values
  soil_poissons_ratio = Math.min(0.49, Math.max(0.01, parseFloat(soil_poissons_ratio))); // Clamp Poisson's ratio between 0.01 and 0.49

  // 1. Calculate Footing Area
  const footing_area_sqm = footing_length_m * footing_width_m;

  // 2. Calculate Actual Bearing Stress
  const actual_bearing_stress_kPa = applied_load_kN / footing_area_sqm;

  // 3. Check Bearing Capacity Exceeded
  const is_bearing_capacity_exceeded = actual_bearing_stress_kPa > allowable_bearing_capacity_kPa;
  const bearing_status_message = is_bearing_capacity_exceeded 
    ? "EXCEEDED (Design likely unsafe)" 
    : "OK (Within allowable limits)";

  // 4. Estimate Elastic Settlement (using a simplified formula for quick check)
  // A common simplified elastic settlement formula for a rigid square/rectangular footing:
  // Settlement = (q_net * B * I_s * (1 - ν^2)) / Es
  // Where:
  // q_net = net bearing pressure (kPa) = actual_bearing_stress_kPa (assuming P is total load, net of soil removed)
  // B = footing width (m)
  // I_s = Shape and Rigidity Influence Factor. For quick check, often approximated.
  // For a rigid footing (L/B up to 5) on a homogeneous elastic half-space, Is can vary from ~0.8 to 1.12. 
  // Let's use a common approximation for a rigid rectangular footing's average settlement, 
  // which can be ~0.85 * (influence factor for flexible corner if needed) for a simplified approach.
  // For a quick check, we'll use a constant I_s = 0.85 for simplicity, representing a typical rigid footing.

  const influence_factor_Is = 0.85; // Simplified influence factor for a rigid footing

  const elastic_settlement_m = (actual_bearing_stress_kPa * footing_width_m * influence_factor_Is * (1 - Math.pow(soil_poissons_ratio, 2))) / soil_elastic_modulus_kPa;

  // Convert settlement from meters to millimeters
  const estimated_settlement_mm = elastic_settlement_m * 1000;

  return {
    actual_bearing_stress_kPa,
    is_bearing_capacity_exceeded: bearing_status_message,
    estimated_settlement_mm
  };
}
