const { initialInvestmentAmount, directJobsCreated, avgDirectEmployeeSalary, localSourcingMultiplier, technologyTransferFactor, projectLifespanYears, avgCorporateTaxRate, avgPersonalIncomeTaxRate, avgIndirectEmployeeSalary = 45000 } = inputs;

if (initialInvestmentAmount <= 0) {
    throw new Error("Initial Investment Amount must be greater than zero.");
}
if (directJobsCreated < 0) {
    throw new Error("Direct Jobs Created cannot be negative.");
}
if (avgDirectEmployeeSalary <= 0) {
    throw new Error("Average Direct Employee Salary must be greater than zero.");
}
if (projectLifespanYears <= 0) {
    throw new Error("Project Lifespan must be greater than zero.");
}
if (localSourcingMultiplier < 1.0) {
    throw new Error("Local Sourcing & Economic Multiplier must be 1.0 or greater.");
}
if (technologyTransferFactor < 0) {
    throw new Error("Technology Transfer Factor cannot be negative.");
}
if (avgCorporateTaxRate < 0 || avgCorporateTaxRate > 100 || avgPersonalIncomeTaxRate < 0 || avgPersonalIncomeTaxRate > 100) {
    throw new Error("Tax rates must be between 0 and 100.");
}
if (avgIndirectEmployeeSalary < 0) {
    throw new Error("Average Indirect Employee Salary cannot be negative.");
}

const corporateTaxRateDecimal = avgCorporateTaxRate / 100;
const personalIncomeTaxRateDecimal = avgPersonalIncomeTaxRate / 100;

// 1. Calculate Direct Economic Contribution
const directWageImpactAnnual = directJobsCreated * avgDirectEmployeeSalary;
const totalDirectWageImpact = directWageImpactAnnual * projectLifespanYears;
const totalDirectEconomicInjection = initialInvestmentAmount + totalDirectWageImpact;

// 2. Calculate Indirect/Induced Economic Impact (Multiplier Effect)
const indirectEffectMultiplier = localSourcingMultiplier - 1.0; // The additional multiplier beyond direct effect
const estimatedIndirectEconomicImpact = totalDirectEconomicInjection * indirectEffectMultiplier;

// 3. Calculate Total Jobs Supported
const totalJobsSupported = Math.round(directJobsCreated * localSourcingMultiplier);

// 4. Calculate Estimated Technology Transfer Value
const estimatedTechnologyTransferValue = initialInvestmentAmount * technologyTransferFactor * projectLifespanYears;

// 5. Calculate Total Tax Revenue Generated
// Simplified corporate tax base: Assume a portion of the project's overall economic generation as a taxable profit base.
// For simplicity, let's use a percentage of the *annual* direct economic injection as a proxy for annual taxable profit.
// A more complex model would involve detailed profit projections.
const estimatedAnnualRevenueBase = (initialInvestmentAmount / projectLifespanYears) + directWageImpactAnnual; // Annualized project value + annual direct wages
const assumedAnnualProfitMargin = 0.15; // Assuming a 15% profit margin for tax purposes (can vary greatly)
const estimatedAnnualCorporateTaxBase = estimatedAnnualRevenueBase * assumedAnnualProfitMargin;
const totalCorporateTax = estimatedAnnualCorporateTaxBase * corporateTaxRateDecimal * projectLifespanYears;

const totalDirectIncomeTax = totalDirectWageImpact * personalIncomeTaxRateDecimal;

const indirectJobsCreated = totalJobsSupported - directJobsCreated;
const totalIndirectWageImpact = indirectJobsCreated * avgIndirectEmployeeSalary * projectLifespanYears;
const totalIndirectIncomeTax = totalIndirectWageImpact * personalIncomeTaxRateDecimal;

let totalTaxRevenueGenerated = totalCorporateTax + totalDirectIncomeTax + totalIndirectIncomeTax;
if (totalTaxRevenueGenerated < 0) totalTaxRevenueGenerated = 0; // Ensure tax revenue is not negative

// 6. Calculate Total Host Economy Benefit
const totalHostEconomyBenefit = totalDirectEconomicInjection + estimatedIndirectEconomicImpact + estimatedTechnologyTransferValue + totalTaxRevenueGenerated;

// 7. Calculate Benefit-to-Cost Ratio (Host Economy ROI)
const benefitToCostRatio = initialInvestmentAmount > 0 ? (totalHostEconomyBenefit / initialInvestmentAmount) : 0;

return {
    totalDirectEconomicInjection: parseFloat(totalDirectEconomicInjection.toFixed(2)),
    estimatedIndirectEconomicImpact: parseFloat(estimatedIndirectEconomicImpact.toFixed(2)),
    totalJobsSupported: Math.round(totalJobsSupported),
    totalTaxRevenueGenerated: parseFloat(totalTaxRevenueGenerated.toFixed(2)),
    estimatedTechnologyTransferValue: parseFloat(estimatedTechnologyTransferValue.toFixed(2)),
    totalHostEconomyBenefit: parseFloat(totalHostEconomyBenefit.toFixed(2)),
    benefitToCostRatio: parseFloat(benefitToCostRatio.toFixed(2))
};
