const I = inputs.initialInvestmentCost || 0;
const D = inputs.trainingDurationMonths || 0; // Months
const CS = inputs.currentAnnualSalary || 0;
const SI = (inputs.postTrainingSalaryIncreasePercentage || 0) / 100;
let L = inputs.careerLongevityYears || 1; // Years, min 1
const T = inputs.timeCommitmentHoursPerWeek || 0;
const AD = (inputs.annualSkillDepreciationRate || 0) / 100;
const DR = (inputs.discountRatePercentage || 0) / 100;

// Ensure L is at least 1 to avoid division by zero or log issues
if (L < 1) L = 1;

// 1. Calculate Opportunity Cost
// Assuming a standard 40-hour work week, 52 weeks per year.
// Hourly rate based on current annual salary.
const annualWorkHours = 52 * 40;
const hourlyRate = CS > 0 ? CS / annualWorkHours : 0;
// Total training weeks over the duration D (months)
const totalTrainingWeeks = D * (52 / 12);
const totalTrainingHours = T * totalTrainingWeeks;
const totalOpportunityCost = hourlyRate * totalTrainingHours;

// 2. Total Investment Cost
const totalInvestmentCost = I + totalOpportunityCost;

// Handle edge case where totalInvestmentCost is zero to prevent division by zero later
if (totalInvestmentCost <= 0) {
    return {
        totalInvestmentCost: totalInvestmentCost,
        totalProjectedBenefit: 0,
        netROI: 0,
        paybackPeriodYears: 0,
        annualizedROI: 0
    };
}

// 3. Calculate Total Projected Benefit (Net Present Value)
let totalProjectedBenefit = 0;
let cumulativeBenefitForPayback = 0;
let paybackPeriodYears = L; // Default to max longevity if not paid back
let paidBack = false;

// We calculate the *additional* salary benefit
const initialAnnualSalaryIncrease = CS * SI;

// If no initial salary increase, then no benefit.
if (initialAnnualSalaryIncrease <= 0) {
    return {
        totalInvestmentCost: totalInvestmentCost,
        totalProjectedBenefit: 0,
        netROI: -100, // 100% loss if no benefit but there was a cost
        paybackPeriodYears: L, // Never pays back
        annualizedROI: -100 // 100% loss over L years
    };
}

for (let year = 1; year <= L; year++) {
    // Benefit in this specific year, considering skill depreciation
    // (1 - AD)^(year - 1) applies depreciation for completed years. For year 1, (1-AD)^0 = 1.
    const benefitThisYear = initialAnnualSalaryIncrease * Math.pow(Math.max(0, (1 - AD)), year - 1);

    // Discount this year's benefit to present value
    // We discount at the end of the year, so (1 + DR)^year
    const discountedBenefitThisYear = benefitThisYear / Math.pow((1 + DR), year);
    totalProjectedBenefit += discountedBenefitThisYear;

    // For payback period, track cumulative discounted benefit.
    if (!paidBack) {
        cumulativeBenefitForPayback += discountedBenefitThisYear;
        if (cumulativeBenefitForPayback >= totalInvestmentCost) {
            // Interpolate for exact payback period
            const previousCumulativeBenefit = cumulativeBenefitForPayback - discountedBenefitThisYear;
            paybackPeriodYears = year - 1 + (totalInvestmentCost - previousCumulativeBenefit) / discountedBenefitThisYear;
            paidBack = true;
        }
    }
}

// 4. Net ROI
let netROI = ((totalProjectedBenefit - totalInvestmentCost) / totalInvestmentCost) * 100;
// Guard against NaN/Infinity, though primary division by zero is handled above.
if (isNaN(netROI) || !isFinite(netROI)) netROI = 0;

// 5. Annualized ROI (Compound Annual Growth Rate)
let annualizedROI = 0;
if (totalProjectedBenefit > 0 && totalInvestmentCost > 0) {
    const ratio = totalProjectedBenefit / totalInvestmentCost;
    // Math.pow(ratio, 1/L) can be NaN if ratio is negative and L is even. But ratio should be non-negative.
    if (ratio >= 0) { 
        annualizedROI = (Math.pow(ratio, 1 / L) - 1) * 100;
    }
} else if (totalProjectedBenefit === 0 && totalInvestmentCost > 0) {
    annualizedROI = -100; // Represents a complete loss over L years if no benefit is generated.
}

return {
    totalInvestmentCost: totalInvestmentCost,
    totalProjectedBenefit: totalProjectedBenefit,
    netROI: netROI,
    paybackPeriodYears: paybackPeriodYears,
    annualizedROI: annualizedROI
};
