const P = parseFloat(paymentAmount);
const r_annual_decimal = parseFloat(annualInterestRate) / 100;
const t = parseFloat(years);
const m_payment = parseInt(paymentFrequency);
const m_compounding = parseInt(compoundingFrequency);
const timing = paymentTiming.toLowerCase();

if (isNaN(P) || isNaN(r_annual_decimal) || isNaN(t) || isNaN(m_payment) || isNaN(m_compounding) || P < 0 || r_annual_decimal < 0 || t < 0 || m_payment <= 0 || m_compounding <= 0) {
    throw new Error("Please enter valid positive numbers for all financial inputs. Payment Timing should be 'beginning' or 'end'.");
}
if (timing !== 'beginning' && timing !== 'end') {
    throw new Error("Payment Timing must be 'beginning' or 'end'.");
}

// Calculate the rate per compounding period
const r_per_compounding_period = r_annual_decimal / m_compounding;

// Calculate the effective interest rate per payment period
// This formula accounts for different compounding and payment frequencies
let i_effective_per_payment;
if (r_annual_decimal === 0) {
    i_effective_per_payment = 0;
} else {
    i_effective_per_payment = Math.pow(1 + r_per_compounding_period, m_compounding / m_payment) - 1;
}

// Total number of payments
const n_total_payments = t * m_payment;

let futureValueAnnuity = 0;

if (i_effective_per_payment === 0) { 
    futureValueAnnuity = P * n_total_payments;
} else {
    const factor = (Math.pow(1 + i_effective_per_payment, n_total_payments) - 1) / i_effective_per_payment;
    futureValueAnnuity = P * factor;

    // Adjust for Annuity Due if payments are at the beginning
    if (timing === 'beginning') {
        futureValueAnnuity *= (1 + i_effective_per_payment);
    }
}

const totalPaymentsMade = P * n_total_payments;
const totalInterestEarned = futureValueAnnuity - totalPaymentsMade;

return {
    futureValueAnnuity: futureValueAnnuity,
    totalPaymentsMade: totalPaymentsMade,
    totalInterestEarned: totalInterestEarned
};
