const P = inputs.paymentAmount; 
const r_annual = inputs.annualInterestRate / 100; // Convert percentage to decimal 
const t = inputs.numberOfYears; 
const m = inputs.paymentsPerYear; 
const isAnnuityDue = inputs.isAnnuityDue === 1; // True if 1, false if 0 

// Calculate rate per period and total number of periods 
const i_per_period = r_annual / m; 
const total_periods = t * m; 

let futureValue; 

// Handle cases where interest rate is 0 to avoid division by zero 
if (i_per_period === 0) { 
    futureValue = P * total_periods; 
} else { 
    // Standard Future Value of Ordinary Annuity formula 
    futureValue = P * ((Math.pow(1 + i_per_period, total_periods) - 1) / i_per_period); 
} 

// Adjust for Annuity Due if applicable 
if (isAnnuityDue) { 
    futureValue *= (1 + i_per_period); 
} 

const totalPayments = P * total_periods; 
const totalInterestEarned = futureValue - totalPayments; 

return { 
    futureValue: futureValue, 
    totalPayments: totalPayments, 
    totalInterestEarned: totalInterestEarned 
};
