function calculate({ initialAmount, annualInterestRate, yearsToGrow, annualInflationRate }) {
  const r = annualInterestRate / 100;
  const i = annualInflationRate / 100;

  // Calculate Nominal Future Value (FV = PV * (1 + r)^n)
  const futureValueNominal = initialAmount * Math.pow((1 + r), yearsToGrow);

  // Calculate Real Future Value (FV_real = FV_nominal / (1 + i)^n)
  const futureValueReal = futureValueNominal / Math.pow((1 + i), yearsToGrow);

  return { futureValueNominal, futureValueReal };
}
