function calculate({ plant_water_requirement_factor, et_rate_mm_day, soil_water_holding_capacity_mm_cm, effective_root_depth_cm, irrigation_system_efficiency_percent, maximum_allowable_depletion_percent, target_application_rate_mm_hr }) {

  // Input validations and default values for robustness, ensuring positive and reasonable ranges
  plant_water_requirement_factor = Math.max(0.1, plant_water_requirement_factor);
  et_rate_mm_day = Math.max(0.1, et_rate_mm_day);
  soil_water_holding_capacity_mm_cm = Math.max(0.1, soil_water_holding_capacity_mm_cm);
  effective_root_depth_cm = Math.max(5, effective_root_depth_cm);
  irrigation_system_efficiency_percent = Math.min(100, Math.max(1, irrigation_system_efficiency_percent));
  maximum_allowable_depletion_percent = Math.min(100, Math.max(10, maximum_allowable_depletion_percent));
  target_application_rate_mm_hr = Math.max(0.1, target_application_rate_mm_hr);

  // Convert percentages to decimal for calculations
  const efficiency_decimal = irrigation_system_efficiency_percent / 100;
  const mad_decimal = maximum_allowable_depletion_percent / 100;

  // 1. Calculate Crop Evapotranspiration (ETc): Actual water demand of your plants
  const etc_mm_day = et_rate_mm_day * plant_water_requirement_factor;

  // 2. Calculate Total Available Water (TAW) in the effective root zone
  const taw_mm = soil_water_holding_capacity_mm_cm * effective_root_depth_cm;

  // 3. Calculate Readily Available Water (RAW): The amount of water plants can use before stress
  const raw_mm = taw_mm * mad_decimal;

  // 4. Net Irrigation Requirement (NIR): The amount of water needed by the plant to replenish RAW
  const net_irrigation_amount_mm = raw_mm;

  // 5. Gross Irrigation Requirement (GIR): The total water the system must apply to deliver NIR, accounting for efficiency
  const gross_irrigation_amount_mm = net_irrigation_amount_mm / efficiency_decimal;

  // 6. Calculate Watering Frequency: How often to water based on RAW and ETc
  let watering_frequency_days = raw_mm / etc_mm_day;
  // Cap frequency for practical gardening, typically between 1 and 30 days
  watering_frequency_days = Math.min(Math.max(Math.round(watering_frequency_days), 1), 30);

  // 7. Calculate Watering Duration: How long to run the system to apply GIR
  const watering_duration_hours = gross_irrigation_amount_mm / target_application_rate_mm_hr;
  let watering_duration_minutes = watering_duration_hours * 60;
  // Ensure a minimum practical duration, e.g., 5 minutes, and round to nearest minute
  watering_duration_minutes = Math.max(Math.round(watering_duration_minutes), 5);

  return {
    watering_frequency_days: watering_frequency_days,
    watering_duration_minutes: watering_duration_minutes
  };
}
