const gardenWidth = inputs.gardenWidth;
const gardenLength = inputs.gardenLength;
const plantSpacingSqFt = inputs.plantSpacingSqFt;
const averageYieldPerPlant = inputs.averageYieldPerPlant;
const successRate = inputs.successRate;
const numberOfHarvestsPerSeason = inputs.numberOfHarvestsPerSeason;

if (gardenWidth <= 0 || gardenLength <= 0 || plantSpacingSqFt <= 0 || averageYieldPerPlant <= 0 || successRate < 0 || successRate > 100 || numberOfHarvestsPerSeason <= 0) {
    throw new Error("Please ensure all input values are positive and valid.");
}

const totalGardenAreaSqFt = gardenWidth * gardenLength;
const maxPlants = Math.floor(totalGardenAreaSqFt / plantSpacingSqFt);

// Apply success rate as a percentage
const effectivePlants = Math.floor(maxPlants * (successRate / 100));

// Calculate total yield, ensuring it's not negative
const estimatedTotalYield = Math.max(0, effectivePlants * averageYieldPerPlant * numberOfHarvestsPerSeason);

return {
    totalGardenAreaSqFt: totalGardenAreaSqFt,
    maxPlants: maxPlants,
    effectivePlants: effectivePlants,
    estimatedTotalYield: estimatedTotalYield
};
